import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

const FILTERS = ["all", "schools", "hospitals", "restaurants", "theaters", "medical shops"];

function App() {
    const [location, setLocation] = useState(null);
    const [places, setPlaces] = useState([]);
    const [filter, setFilter] = useState("all");

    useEffect(() => {
        navigator.geolocation.getCurrentPosition(
            (pos) => setLocation({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
            (err) => console.error("Geolocation error:", err)
        );
    }, []);

    useEffect(() => {
        if (location) {
            fetchPlaces();
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [location, filter]);

    const fetchPlaces = async () => {
        try {
            const response = await axios.get("http://localhost:5000/api/places", {
                params: {
                    latitude: location.lat,
                    longitude: location.lng,
                    type: filter,
                },
            });
            setPlaces(response.data);
        } catch (error) {
            console.error("Error fetching places:", error);
        }
    };

    return (
        <div className="app">
            <header>
                <span role="img" aria-label="globe" className="logo">üåç</span>
                <h1>Local Explorer</h1>
            </header>

            <input
                type="text"
                value={location ? `${location.lat}, ${location.lng}` : "Fetching location..."}
                disabled
            />

            <div className="filters">
                {FILTERS.map((item) => (
                    <button
                        key={item}
                        className={filter === item ? "active" : ""}
                        onClick={() => setFilter(item)}
                    >
                        {item.toUpperCase()}
                    </button>
                ))}
            </div>

            <div className="grid">
                {places.length === 0 && <p>No places found nearby.</p>}
                {places.map((place) => (
                    <div className="card" key={place.place_id}>
                        <img
                            src={
                                place.photos
                                    ? `https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photoreference=${place.photos[0].photo_reference}&key=YOUR_GOOGLE_API_KEY`
                                    : "https://via.placeholder.com/400x300?text=No+Image"
                            }
                            alt={place.name}
                        />
                        <div className="card-info">
                            <h2>{place.name}</h2>
                            <p>{place.vicinity}</p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default App;
