import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [places, setPlaces] = useState([]);
  const [filteredPlaces, setFilteredPlaces] = useState([]);
  const [location, setLocation] = useState("");
  const [selectedFilter, setSelectedFilter] = useState("ALL");

  // Mapping your button labels to Google Place types
  const filterMap = {
    ALL: null,
    SCHOOLS: "school",
    HOSPITALS: "hospital",
    RESTAURANTS: "restaurant",
    THEATERS: "movie_theater",
    "MEDICAL SHOPS": "pharmacy",
  };

  useEffect(() => {
    if (location.trim() === "") {
      setPlaces([]);
      setFilteredPlaces([]);
      return;
    }

    const fetchPlaces = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5000/api/places?location=${location}`
        );
        console.log("API Response:", response.data); // Debug log
        setPlaces(response.data);
        applyFilter(response.data, selectedFilter);
      } catch (error) {
        console.error("Error fetching places:", error);
      }
    };

    fetchPlaces();
  }, [location]);

  const applyFilter = (data, filter) => {
    const type = filterMap[filter];
    if (!type) {
      setFilteredPlaces(data); // Show all if filter is ALL
    } else {
      const filtered = data.filter((place) =>
        place.types.includes(type)
      );
      if (filtered.length > 0) {
        setFilteredPlaces(filtered);
      } else {
        console.warn("No results for filter:", filter);
        setFilteredPlaces(data); // fallback to all places
      }
    }
  };

  const handleFilterChange = (filter) => {
    setSelectedFilter(filter);
    applyFilter(places, filter);
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1 style={{ color: "dodgerblue" }}>üåé Local Explorer</h1>
      <input
        type="text"
        placeholder="Search a location e.g. Rampally"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
        style={{
          width: "60%",
          padding: "10px",
          fontSize: "16px",
          marginBottom: "15px",
        }}
      />
      <div style={{ marginBottom: "20px" }}>
        {Object.keys(filterMap).map((filter) => (
          <button
            key={filter}
            onClick={() => handleFilterChange(filter)}
            style={{
              margin: "5px",
              padding: "10px 15px",
              backgroundColor:
                selectedFilter === filter ? "#007BFF" : "#e0e0e0",
              color: selectedFilter === filter ? "#fff" : "#000",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            {filter}
          </button>
        ))}
      </div>
      {filteredPlaces.length === 0 ? (
        <p>No places found for this filter/location.</p>
      ) : (
        <ul style={{ listStyle: "none", padding: 0 }}>
          {filteredPlaces.map((place) => (
            <li
              key={place.place_id}
              style={{
                margin: "10px auto",
                padding: "10px",
                border: "1px solid #ccc",
                borderRadius: "8px",
                width: "70%",
                textAlign: "left",
              }}
            >
              <h3>{place.name}</h3>
              <p>{place.formatted_address}</p>
              <p>‚≠ê Rating: {place.rating || "N/A"}</p>
              <p>Type: {place.types.join(", ")}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
