import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  TextField,
  Card,
  CardContent,
  Typography,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  Button,
  Grid,
} from "@mui/material";
import { Loader } from "@googlemaps/js-api-loader";
import { motion } from "framer-motion";

const App = () => {
  const [places, setPlaces] = useState([]); // Always an array
  const [loading, setLoading] = useState(false);
  const [selectedPlace, setSelectedPlace] = useState(null);

  const fetchPlaces = async (location) => {
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:5000/api/places?location=${location}`
      );
      console.log("API Response:", response.data);

      if (response.data && Array.isArray(response.data.results)) {
        setPlaces(response.data.results);
      } else {
        setPlaces([]); // fallback if API doesn't send expected results
      }
    } catch (error) {
      console.error("Error fetching places:", error);
      setPlaces([]); // fallback to empty array
    }
    setLoading(false);
  };

  useEffect(() => {
    const loader = new Loader({
      apiKey: "AIzaSyB3PgI153B24mtSoJvKSjCPQyEyDQqu3K8", // your API key
      libraries: ["places"],
    });

    loader.load().then(() => {
      const input = document.getElementById("locationInput");
      const autocomplete = new window.google.maps.places.Autocomplete(input);
      autocomplete.addListener("place_changed", () => {
        const place = autocomplete.getPlace();
        if (place.formatted_address) {
          fetchPlaces(place.formatted_address);
        }
      });
    });
  }, []);


  const handleCardClick = async (place) => {
  setLoading(true);
  try {
    const response = await axios.get(
      `http://localhost:5000/api/place-details/${place.place_id}`
    );
    setSelectedPlace(response.data);
  } catch (error) {
    console.error("Error fetching place details:", error);
  }
  setLoading(false);
};


  return (
    <div style={{ minHeight: "100vh", background: "#f0f2f5", padding: "2rem" }}>
      <Typography variant="h3" align="center" style={{ marginBottom: "2rem" }}>
        🌍 Local Explorer
      </Typography>

      <div style={{ display: "flex", justifyContent: "center", marginBottom: "2rem" }}>
        <TextField
          id="locationInput"
          placeholder="Search a location e.g. Rampally"
          variant="outlined"
          fullWidth
          style={{ maxWidth: 600, backgroundColor: "#fff", borderRadius: 8 }}
        />
      </div>

      {loading ? (
        <div style={{ display: "flex", justifyContent: "center", marginTop: "3rem" }}>
          <CircularProgress />
        </div>
      ) : (
        <Grid container spacing={3}>
          {places.length > 0 ? (
            places.map((place, idx) => (
              <Grid item xs={12} sm={6} md={4} key={idx}>
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: idx * 0.1 }}
                >
                  <Card
                    style={{
                      borderRadius: 16,
                      boxShadow: "0 4px 20px rgba(0,0,0,0.2)",
                      cursor: "pointer",
                    }}
                    onClick={() => handleCardClick(place)}
                  >
                    <img
                      src={
                        place.photos
                          ? `https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photoreference=${place.photos[0].photo_reference}&key=AIzaSyB3PgI153B24mtSoJvKSjCPQyEyDQqu3K8`
                          : "https://via.placeholder.com/400x300?text=No+Image"
                      }
                      alt={place.name}
                      style={{
                        width: "100%",
                        height: 200,
                        objectFit: "cover",
                        borderTopLeftRadius: 16,
                        borderTopRightRadius: 16,
                      }}
                      onError={(e) => {
                        e.target.src = "https://via.placeholder.com/400x300?text=No+Image";
                      }}
                    />
                    <CardContent>
                      <Typography variant="h6">{place.name}</Typography>
                      <Typography variant="body2" color="textSecondary">
                        {place.formatted_address}
                      </Typography>
                    </CardContent>
                  </Card>
                </motion.div>
              </Grid>
            ))
          ) : (
            <Typography variant="h6" align="center" style={{ marginTop: "2rem" }}>
              No places found. Try another location.
            </Typography>
          )}
        </Grid>
      )}

      <Dialog open={!!selectedPlace} onClose={handleClose} fullWidth maxWidth="sm">
        <DialogTitle>{selectedPlace?.name}</DialogTitle>
        <DialogContent>
          <Typography variant="body1" gutterBottom>
            Address: {selectedPlace?.formatted_address}
          </Typography>
          <Typography variant="body2" color="textSecondary" gutterBottom>
            Showing reviews:
          </Typography>
          {selectedPlace?.reviews ? (
            <ul>
              {selectedPlace.reviews.map((review, index) => (
                <li key={index}>
                  <strong>{review.author_name}:</strong> {review.text}
                </li>
              ))}
            </ul>
          ) : (
            <Typography variant="body2">No reviews available.</Typography>
          )}
        </DialogContent>
        <Button onClick={handleClose} style={{ margin: "1rem" }} variant="contained" color="primary">
          Close
        </Button>
      </Dialog>
    </div>
  );
};

export default App;
