import React, { useState, useEffect } from "react";
import axios from "axios";
import { TextField, Card, CardContent, Typography, CircularProgress } from "@mui/material";
import { Loader } from "@googlemaps/js-api-loader";
import { motion } from "framer-motion";

const App = () => {
  const [query, setQuery] = useState("");
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchPlaces = async (location) => {
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:5000/api/places?location=${location}`
      );
      setPlaces(response.data.results);
    } catch (error) {
      console.error("Error fetching places:", error);
    }
    setLoading(false);
  };

  useEffect(() => {
    const loader = new Loader({
      apiKey: "AIzaSyB3PgI153B24mtSoJvKSjCPQyEyDQqu3K8",
      libraries: ["places"],
    });

    loader.load().then(() => {
      const input = document.getElementById("locationInput");
      const autocomplete = new window.google.maps.places.Autocomplete(input);
      autocomplete.addListener("place_changed", () => {
        const place = autocomplete.getPlace();
        if (place.formatted_address) {
          setQuery(place.formatted_address);
          fetchPlaces(place.formatted_address);
        }
      });
    });
  }, []);

  return (
    <div style={{ minHeight: "100vh", background: "linear-gradient(45deg, #6a11cb, #2575fc)", padding: "2rem" }}>
      <Typography variant="h3" align="center" style={{ color: "#fff", marginBottom: "2rem" }}>
        üåç Local Explorer
      </Typography>

      <div style={{ display: "flex", justifyContent: "center", marginBottom: "2rem" }}>
        <TextField
          id="locationInput"
          placeholder="Search for a location..."
          variant="outlined"
          fullWidth
          style={{ maxWidth: 600, backgroundColor: "#fff", borderRadius: 8 }}
        />
      </div>

      {loading ? (
        <div style={{ display: "flex", justifyContent: "center", marginTop: "3rem" }}>
          <CircularProgress style={{ color: "#fff" }} />
        </div>
      ) : (
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))",
            gap: "1.5rem",
          }}
        >
          {places.map((place, idx) => (
            <motion.div
              key={idx}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: idx * 0.1 }}
            >
              <Card style={{ borderRadius: 16, boxShadow: "0 4px 20px rgba(0,0,0,0.3)" }}>
                <img
                  src={
                    place.photos
                      ? `https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photoreference=${place.photos[0].photo_reference}&key=AIzaSyB3PgI153B24mtSoJvKSjCPQyEyDQqu3K8`
                      : "https://via.placeholder.com/400x300?text=No+Image"
                  }
                  alt={place.name}
                  style={{ width: "100%", height: 200, objectFit: "cover", borderTopLeftRadius: 16, borderTopRightRadius: 16 }}
                  onError={(e) => {
                    e.target.src = "https://via.placeholder.com/400x300?text=No+Image";
                  }}
                />
                <CardContent>
                  <Typography variant="h6">{place.name}</Typography>
                  <Typography variant="body2" color="textSecondary">
                    {place.formatted_address}
                  </Typography>
                </CardContent>
              </Card>
            </motion.div>
          ))}
        </div>
      )}
    </div>
  );
};

export default App;
