import React, { useState, useEffect } from "react";
import axios from "axios";

const CATEGORIES = ["schools", "colleges", "hospitals", "theaters", "medical shops"];
const DEFAULT_IMAGE = "https://source.unsplash.com/400x300/?locality,street,city";

function App() {
  const [location, setLocation] = useState("");
  const [places, setPlaces] = useState({});
  const [loading, setLoading] = useState(false);
  const [selectedPlace, setSelectedPlace] = useState(null);

  const fetchPlaces = async (loc) => {
    if (!loc) return alert("Please enter a location");
    setLoading(true);
    try {
      const res = await axios.get(`http://localhost:5000/api/places?location=${loc}`);
      setPlaces(res.data);
    } catch (err) {
      console.error("Error fetching places:", err);
      alert("Failed to fetch places.");
    }
    setLoading(false);
  };

  const fetchPlaceDetails = async (placeId) => {
    try {
      const res = await axios.get(`http://localhost:5000/api/place-details?place_id=${placeId}`);
      setSelectedPlace(res.data);
    } catch (err) {
      console.error("Error fetching place details:", err);
    }
  };

  const detectLocationAndFetch = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          try {
            const res = await axios.get(
              `https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&key=YOUR_GOOGLE_API_KEY`
            );
            const detectedLocation = res.data.results[0]?.formatted_address || "";
            setLocation(detectedLocation);
            fetchPlaces(detectedLocation);
          } catch (err) {
            console.error("Error detecting location:", err);
          }
        },
        (err) => {
          console.error("Geolocation error:", err);
        }
      );
    } else {
      alert("Geolocation not supported");
    }
  };

 useEffect(() => {
  const fetch = async () => {
    await detectLocationAndFetch();
  };
  fetch();
  // eslint-disable-next-line react-hooks/exhaustive-deps
}, []);

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      <h1 className="text-4xl font-bold text-center text-purple-600 mb-6">
        🗺️ Local Explorer
      </h1>

      <div className="flex justify-center mb-6">
        <input
          type="text"
          placeholder="Enter location (e.g., Hyderabad)"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="border border-gray-300 rounded-l px-4 py-2 w-1/3"
        />
        <button
          onClick={() => fetchPlaces(location)}
          className="bg-purple-600 text-white px-4 py-2 rounded-r hover:bg-purple-700"
        >
          Search
        </button>
      </div>

      {loading && <p className="text-center text-lg text-gray-600">Loading places...</p>}

      {CATEGORIES.map((category) => (
        <div key={category} className="mb-8">
          <h2 className="text-2xl font-semibold text-gray-800 mb-4 capitalize">
            {category}
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {places[category]?.map((place, idx) => (
              <div
                key={idx}
                className="bg-white rounded-xl shadow hover:shadow-lg transition cursor-pointer"
                onClick={() => fetchPlaceDetails(place.place_id)}
              >
                <img
                  src={place.photo_url || DEFAULT_IMAGE}
                  alt={place.name}
                  className="w-full h-48 object-cover rounded-t-xl"
                  onError={(e) => (e.target.src = DEFAULT_IMAGE)}
                />
                <div className="p-4">
                  <h3 className="font-bold text-lg text-gray-700">{place.name}</h3>
                  <p className="text-gray-500">{place.formatted_address}</p>
                  {place.rating && (
                    <p className="text-yellow-500 font-semibold">⭐ {place.rating}</p>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}

      {/* Modal for Place Details */}
      {selectedPlace && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50"
          onClick={() => setSelectedPlace(null)}
        >
          <div
            className="bg-white rounded-lg shadow p-6 w-11/12 md:w-2/3 lg:w-1/2"
            onClick={(e) => e.stopPropagation()}
          >
            <h2 className="text-2xl font-bold mb-2">{selectedPlace.name}</h2>
            <p className="mb-2">{selectedPlace.formatted_address}</p>
            {selectedPlace.rating && (
              <p className="text-yellow-500 mb-2">⭐ {selectedPlace.rating}</p>
            )}
            <h3 className="text-xl font-semibold mb-2">Customer Reviews:</h3>
            {selectedPlace.reviews ? (
              selectedPlace.reviews.map((review, idx) => (
                <div key={idx} className="mb-3 border-b pb-2">
                  <p className="font-bold">{review.author_name}</p>
                  <p className="text-yellow-500">⭐ {review.rating}</p>
                  <p className="text-gray-700">{review.text}</p>
                </div>
              ))
            ) : (
              <p className="text-gray-500">No reviews available.</p>
            )}
            <button
              className="mt-4 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              onClick={() => setSelectedPlace(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
