import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [location, setLocation] = useState("");
  const [places, setPlaces] = useState({});
  const [loading, setLoading] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [loadedImages, setLoadedImages] = useState({});
  const [imageErrors, setImageErrors] = useState({});

  const fetchPlaces = async () => {
    if (!location) return alert("Please enter a location");
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:5000/api/places?location=${location}`
      );
      setPlaces(response.data);
      // Reset image states when fetching new places
      setLoadedImages({});
      setImageErrors({});
    } catch (error) {
      console.error("Error fetching places:", error);
      alert("Failed to fetch places.");
    }
    setLoading(false);
  };

  useEffect(() => {
    // Auto-detect location
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          try {
            const response = await axios.get(
              `https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&key=AIzaSyDki4k3wRgR7TEw1ugkkaWqe1tBxxnDfDk`
            );
            const userLocation = response.data.results[0].formatted_address;
            setLocation(userLocation);
            fetchPlaces();
          } catch (error) {
            console.error("Geolocation error:", error);
          }
        },
        (err) => {
          console.error("Geolocation permission denied:", err);
        }
      );
    }
  }, []);

  const categories = ["schools", "colleges", "theaters", "medical shops"];
  const categoryIcons = {
    schools: "🏫",
    colleges: "🎓",
    theaters: "🎬",
    "medical shops": "💊",
  };

  const handleFilterClick = (category) => {
    setSelectedCategory(category);
  };

  const handleImageError = (placeId, category, location) => {
    setImageErrors(prev => ({
      ...prev,
      [placeId]: `https://source.unsplash.com/400x300/?${category},${location}`
    }));
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-blue-100 p-4">
      <h1 className="text-4xl font-bold text-center mb-8 text-blue-700">
        🌎 Local Explorer
      </h1>

      {/* Search Bar */}
      <div className="flex justify-center mb-6">
        <input
          type="text"
          placeholder="Enter location (e.g., Hyderabad)"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="border border-blue-300 rounded-l px-4 py-2 w-1/2 shadow focus:outline-none focus:ring-2 focus:ring-blue-500"
          onKeyPress={(e) => e.key === "Enter" && fetchPlaces()}
        />
        <button
          onClick={fetchPlaces}
          className="bg-blue-600 text-white px-5 py-2 rounded-r shadow hover:bg-blue-700 transition-colors"
        >
          Search
        </button>
      </div>

      {/* Filter Buttons */}
      <div className="flex justify-center mb-8 flex-wrap gap-2">
        <button
          onClick={() => handleFilterClick("all")}
          className={`px-4 py-2 rounded-full transition-colors ${
            selectedCategory === "all"
              ? "bg-blue-600 text-white shadow-md"
              : "bg-white text-blue-700 border border-blue-300 hover:bg-blue-50"
          }`}
        >
          🌟 All
        </button>
        {categories.map((cat) => (
          <button
            key={cat}
            onClick={() => handleFilterClick(cat)}
            className={`px-4 py-2 rounded-full capitalize transition-colors ${
              selectedCategory === cat
                ? "bg-blue-600 text-white shadow-md"
                : "bg-white text-blue-700 border border-blue-300 hover:bg-blue-50"
            }`}
          >
            {categoryIcons[cat]} {cat}
          </button>
        ))}
      </div>

      {/* Loading */}
      {loading && (
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-600 mb-2"></div>
          <p className="text-lg text-blue-700">Loading places...</p>
        </div>
      )}

      {/* Places Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {Object.entries(places)
          .filter(
            ([category]) => selectedCategory === "all" || selectedCategory === category
          )
          .map(([category, placesArray]) => (
            <div key={category}>
              <h2 className="text-2xl font-semibold text-blue-800 mb-3 capitalize flex items-center">
                <span className="mr-2">{categoryIcons[category]}</span>
                {category}
              </h2>
              <div className="grid grid-cols-1 gap-4">
                {placesArray.map((place) => (
                  <div
                    key={place.place_id || place.id}
                    className="bg-white rounded-xl shadow-lg hover:shadow-2xl transition duration-300 overflow-hidden"
                  >
                    <div className="relative h-48 bg-gray-100">
                      <img
                        src={imageErrors[place.place_id] || place.photo_url}
                        alt={place.name}
                        className={`w-full h-full object-cover transition-opacity ${
                          loadedImages[place.place_id] ? 'opacity-100' : 'opacity-0'
                        }`}
                        onLoad={() => setLoadedImages(prev => ({...prev, [place.place_id]: true}))}
                        onError={() => handleImageError(place.place_id, category, location)}
                      />
                      {!loadedImages[place.place_id] && !imageErrors[place.place_id] && (
                        <div className="absolute inset-0 flex items-center justify-center">
                          <div className="animate-pulse bg-gray-200 w-full h-full"></div>
                        </div>
                      )}
                    </div>
                    <div className="p-4">
                      <h3 className="text-lg font-bold text-gray-800 mb-1">{place.name}</h3>
                      <p className="text-gray-600 text-sm mb-2 line-clamp-2">
                        {place.formatted_address}
                      </p>
                      {place.rating && (
                        <div className="flex items-center">
                          <span className="text-yellow-500 mr-1">⭐</span>
                          <span className="text-sm font-medium">{place.rating}</span>
                        </div>
                      )}
                      {place.is_fallback && (
                        <p className="text-xs text-gray-400 mt-2">Generic image shown</p>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
      </div>

      {!loading && Object.keys(places).length === 0 && (
        <div className="text-center py-10">
          <p className="text-gray-500">No places found. Try searching for a location.</p>
        </div>
      )}
    </div>
  );
}

export default App;
