import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";

function App() {
  const [location, setLocation] = useState("");
  const [places, setPlaces] = useState({});
  const [loading, setLoading] = useState(false);
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [activeCategory, setActiveCategory] = useState("all");

  const fetchPlaces = useCallback(async (loc = location) => {
    if (!loc) return alert("Please enter a location");
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:5000/api/places?location=${loc}`
      );
      setPlaces(response.data);
    } catch (error) {
      console.error("Error fetching places:", error);
      alert("Failed to fetch places.");
    }
    setLoading(false);
  }, [location]);

  const fetchPlaceDetails = async (placeId) => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/place-details?place_id=${placeId}`
      );
      setSelectedPlace(response.data);
    } catch (error) {
      console.error("Error fetching place details:", error);
      alert("Failed to fetch place details.");
    }
  };

  useEffect(() => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          try {
            const geoRes = await axios.get(
              `https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&key=AIzaSyDki4k3wRgR7TEw1ugkkaWqe1tBxxnDfDk`
            );
            const detectedLocation = geoRes.data.results[0].formatted_address;
            setLocation(detectedLocation);
            fetchPlaces(detectedLocation);
          } catch (err) {
            console.error("Failed to detect location:", err);
          }
        },
        (err) => {
          console.error("Geolocation error:", err);
          alert("Failed to detect location automatically.");
        }
      );
    }
  }, [fetchPlaces]);

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-gray-100 p-4">
      <h1 className="text-4xl font-extrabold text-center text-blue-700 mb-6">
        🗺️ Local Explorer
      </h1>

      {/* 📦 Category Tabs */}
      <div className="flex justify-center flex-wrap gap-3 mb-5">
        {["all", "schools", "colleges", "hospitals", "theaters", "medical shops"].map((cat) => (
          <button
            key={cat}
            onClick={() => setActiveCategory(cat)}
            className={`px-4 py-2 rounded-full border shadow-md hover:shadow-lg transition ${
              activeCategory === cat
                ? "bg-blue-600 text-white border-blue-700"
                : "bg-white text-gray-700 border-gray-300"
            }`}
          >
            {cat.toUpperCase()}
          </button>
        ))}
      </div>

      {/* 🔍 Search Bar */}
      <div className="flex justify-center mb-6">
        <input
          type="text"
          placeholder="Enter location (e.g., Rampally)"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="border border-gray-300 rounded-l-full px-4 py-2 w-1/2 focus:outline-none focus:ring focus:border-blue-300"
        />
        <button
          onClick={() => fetchPlaces()}
          className="bg-blue-600 text-white px-6 py-2 rounded-r-full hover:bg-blue-700"
        >
          Search
        </button>
      </div>

      {loading && (
        <p className="text-center text-lg font-medium text-gray-600">
          🔄 Loading places...
        </p>
      )}

      {/* 📍 Places Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {Object.entries(places)
          .filter(([category]) => activeCategory === "all" || category === activeCategory)
          .map(([category, placesArray]) => (
            <div key={category}>
              <h2 className="text-2xl font-semibold mb-4 capitalize text-blue-800">
                {category}
              </h2>
              {placesArray.map((place, index) => (
                <div
                  key={index}
                  className="bg-white rounded-xl shadow-lg hover:shadow-2xl transition cursor-pointer"
                  onClick={() => fetchPlaceDetails(place.place_id)}
                >
                  <img
                    src={place.photo_url || "https://source.unsplash.com/400x300/?city,place"}
                    alt={place.name}
                    className="w-full h-48 object-cover rounded-t-xl"
                    onError={(e) =>
                      (e.target.src = "https://source.unsplash.com/400x300/?city,place")
                    }
                  />
                  <div className="p-4">
                    <h3 className="text-lg font-bold text-gray-800">{place.name}</h3>
                    <p className="text-gray-600">{place.formatted_address}</p>
                    {place.rating && (
                      <p className="text-yellow-500 mt-2">⭐ {place.rating}</p>
                    )}
                  </div>
                </div>
              ))}
            </div>
          ))}
      </div>

      {/* 📌 Modal */}
      {selectedPlace && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50"
          onClick={() => setSelectedPlace(null)}
        >
          <div
            className="bg-white rounded-2xl shadow-lg p-6 w-11/12 md:w-2/3 lg:w-1/2"
            onClick={(e) => e.stopPropagation()}
          >
            <h2 className="text-3xl font-bold mb-2">{selectedPlace.name}</h2>
            <p className="mb-2 text-gray-700">{selectedPlace.formatted_address}</p>
            {selectedPlace.rating && (
              <p className="text-yellow-500 mb-4">⭐ {selectedPlace.rating}</p>
            )}

            <h3 className="text-xl font-semibold mb-2">Customer Reviews:</h3>
            {selectedPlace.reviews ? (
              selectedPlace.reviews.map((review, idx) => (
                <div key={idx} className="mb-3 border-b pb-2">
                  <p className="font-bold">{review.author_name}</p>
                  <p className="text-yellow-500">⭐ {review.rating}</p>
                  <p className="text-gray-700">{review.text}</p>
                </div>
              ))
            ) : (
              <p className="text-gray-500">No reviews available.</p>
            )}

            <button
              className="mt-4 bg-red-500 text-white px-5 py-2 rounded-full hover:bg-red-600"
              onClick={() => setSelectedPlace(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
