import React, { useState, useEffect } from "react";
import axios from "axios";
import { Input } from "@shadcn/ui/input";
import { Card, CardContent, CardTitle } from "@shadcn/ui/card";
import { Loader } from "@googlemaps/js-api-loader";
import { motion } from "framer-motion";

const App = () => {
  const [query, setQuery] = useState("");
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch places
  const fetchPlaces = async (location) => {
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:5000/api/places?location=${location}`
      );
      setPlaces(response.data.results);
    } catch (error) {
      console.error("Error fetching places:", error);
    }
    setLoading(false);
  };

  // Load Google Autocomplete
  useEffect(() => {
    const loader = new Loader({
      apiKey: "AIzaSyB3PgI153B24mtSoJvKSjCPQyEyDQqu3K8",
      libraries: ["places"],
    });

    loader.load().then(() => {
      const input = document.getElementById("locationInput");
      const autocomplete = new window.google.maps.places.Autocomplete(input);
      autocomplete.addListener("place_changed", () => {
        const place = autocomplete.getPlace();
        if (place.formatted_address) {
          setQuery(place.formatted_address);
          fetchPlaces(place.formatted_address);
        }
      });
    });
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-r from-purple-500 to-pink-500 p-8">
      <h1 className="text-4xl font-bold text-white text-center mb-6">üåç Local Explorer</h1>
      <div className="flex justify-center">
        <Input
          id="locationInput"
          placeholder="Search for a location..."
          className="w-full max-w-lg text-lg p-3 rounded-xl shadow-md"
        />
      </div>

      {loading ? (
        <div className="flex justify-center items-center mt-10">
          <motion.div
            className="w-16 h-16 border-4 border-dashed rounded-full animate-spin border-white"
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ duration: 0.5 }}
          />
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-10">
          {places.map((place, idx) => (
            <motion.div
              key={idx}
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: idx * 0.1 }}
            >
              <Card className="rounded-2xl shadow-xl hover:scale-105 transition">
                <img
                  src={
                    place.photos
                      ? `https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photoreference=${place.photos[0].photo_reference}&key=AIzaSyB3PgI153B24mtSoJvKSjCPQyEyDQqu3K8`
                      : "https://via.placeholder.com/400x300?text=No+Image"
                  }
                  alt={place.name}
                  className="rounded-t-2xl h-48 w-full object-cover"
                  onError={(e) => {
                    e.target.src =
                      "https://via.placeholder.com/400x300?text=No+Image";
                  }}
                />
                <CardContent>
                  <CardTitle>{place.name}</CardTitle>
                  <p className="text-gray-600">{place.formatted_address}</p>
                </CardContent>
              </Card>
            </motion.div>
          ))}
        </div>
      )}
    </div>
  );
};

export default App;
