import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  GoogleMap,
  LoadScript,
  Marker,
  InfoWindow,
} from "@react-google-maps/api";

const API_KEY = "AIzaSyB3PgI153B24mtSoJvKSjCPQyEyDQqu3K8"; // Your Google API Key

function App() {
  const [location, setLocation] = useState("");
  const [places, setPlaces] = useState({});
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [activeCategory, setActiveCategory] = useState("all");

  const mapContainerStyle = {
    width: "100%",
    height: "80vh",
  };

  const center = {
    lat: 17.4672,
    lng: 78.6419,
  };

  const fetchPlaces = async (loc) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/places`, {
        params: { location: loc },
      });
      setPlaces(response.data);
      setSelectedPlace(null);
    } catch (error) {
      console.error("Error fetching places:", error);
    }
  };

  const fetchPlaceDetails = async (place_id) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/place-details`, {
        params: { place_id },
      });
      setSelectedPlace(response.data);
    } catch (error) {
      console.error("Error fetching place details:", error);
    }
  };

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;

          const geocodeRes = await axios.get(
            `https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&key=${API_KEY}`
          );

          const locName = geocodeRes.data.results[0]?.formatted_address || "Unknown Location";
          setLocation(locName);
          fetchPlaces(locName);
        },
        (err) => {
          console.error("Geolocation error:", err);
          alert("Could not get your location. Please enter manually.");
        }
      );
    } else {
      alert("Geolocation is not supported by this browser.");
    }
  }, []);

  const handleCategoryFilter = (category) => {
    setActiveCategory(category);
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <h1 className="text-3xl font-bold text-center py-4">🌏 Local Explorer</h1>

      {/* Search */}
      <div className="flex justify-center mb-4">
        <input
          type="text"
          placeholder="Enter location (e.g., Rampally)"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="border rounded px-4 py-2 w-80 mr-2"
        />
        <button
          onClick={() => fetchPlaces(location)}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Search
        </button>
      </div>

      {/* Filters */}
      <div className="flex justify-center space-x-2 mb-2">
        <button
          onClick={() => handleCategoryFilter("all")}
          className={`px-3 py-1 rounded ${activeCategory === "all" ? "bg-blue-500 text-white" : "bg-gray-300"}`}
        >
          All
        </button>
        {Object.keys(places).map((category) => (
          <button
            key={category}
            onClick={() => handleCategoryFilter(category)}
            className={`px-3 py-1 rounded ${activeCategory === category ? "bg-blue-500 text-white" : "bg-gray-300"}`}
          >
            {category}
          </button>
        ))}
      </div>

      {/* Map + List */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 p-4">
        {/* List */}
        <div className="overflow-y-auto h-[80vh]">
          {Object.keys(places).map((category) => (
            (activeCategory === "all" || activeCategory === category) && (
              <div key={category} className="mb-4">
                <h2 className="text-xl font-bold mb-2 capitalize">{category}</h2>
                <div className="space-y-2">
                  {places[category].map((place) => (
                    <div
                      key={place.place_id}
                      className="bg-white rounded shadow p-2 cursor-pointer hover:bg-gray-50"
                      onClick={() => fetchPlaceDetails(place.place_id)}
                    >
                      <img
                        src={place.photo_url}
                        alt={place.name}
                        onError={(e) => {
                          if (!e.target.dataset.fallback) {
                            e.target.src = "https://via.placeholder.com/400x300.png?text=No+Image";
                            e.target.dataset.fallback = true;
                          }
                        }}
                        className="w-full h-40 object-cover rounded mb-1"
                      />
                      <h3 className="font-medium">{place.name}</h3>
                      <p className="text-gray-600">{place.formatted_address}</p>
                      <p className="text-yellow-500">⭐ {place.rating}</p>
                    </div>
                  ))}
                </div>
              </div>
            )
          ))}
        </div>

        {/* Map */}
        <div>
          <LoadScript googleMapsApiKey={API_KEY}>
            <GoogleMap
              mapContainerStyle={mapContainerStyle}
              center={center}
              zoom={14}
            >
              {Object.values(places).flat().map((place) => (
                <Marker
                  key={place.place_id}
                  position={place.geometry.location}
                  onClick={() => fetchPlaceDetails(place.place_id)}
                />
              ))}

              {selectedPlace && (
                <InfoWindow
                  position={selectedPlace.geometry.location}
                  onCloseClick={() => setSelectedPlace(null)}
                >
                  <div>
                    <h2 className="font-bold">{selectedPlace.name}</h2>
                    <p>{selectedPlace.formatted_address}</p>
                    <p className="text-yellow-500">⭐ {selectedPlace.rating}</p>
                  </div>
                </InfoWindow>
              )}
            </GoogleMap>
          </LoadScript>
        </div>
      </div>
    </div>
  );
}

export default App;
