import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

const filters = ["all", "schools", "hospitals", "restaurants", "theaters", "medical shops"];

function App() {
    const [currentLocation, setCurrentLocation] = useState(null);
    const [places, setPlaces] = useState([]);
    const [filterType, setFilterType] = useState("all");

    useEffect(() => {
        navigator.geolocation.getCurrentPosition(
            (position) => {
                const { latitude, longitude } = position.coords;
                setCurrentLocation({ latitude, longitude });
            },
            (error) => {
                console.error("Error getting location:", error.message);
            }
        );
    }, []);

    useEffect(() => {
        if (currentLocation) {
            fetchPlaces(currentLocation.latitude, currentLocation.longitude, filterType);
        }
    }, [currentLocation, filterType]);

    const fetchPlaces = async (latitude, longitude, type) => {
        try {
            const response = await axios.get("http://localhost:5000/api/places", {
                params: { latitude, longitude, type }
            });
            setPlaces(response.data);
        } catch (error) {
            console.error("Error fetching places:", error.message);
        }
    };

    return (
        <div className="app">
            <header className="header">
                <span role="img" aria-label="globe" className="logo">üåç</span>
                <h1 className="title">Local Explorer</h1>
            </header>
            <input
                className="location-input"
                placeholder="Fetching your location..."
                value={
                    currentLocation
                        ? `Lat: ${currentLocation.latitude}, Lng: ${currentLocation.longitude}`
                        : ""
                }
                disabled
            />
            <div className="filter-buttons">
                {filters.map((type) => (
                    <button
                        key={type}
                        className={`filter-btn ${filterType === type ? "active" : ""}`}
                        onClick={() => setFilterType(type)}
                    >
                        {type.toUpperCase()}
                    </button>
                ))}
            </div>
            <div className="places-grid">
                {places.map((place) => (
                    <div className="place-card" key={place.place_id}>
                        <img
                            src={
                                place.photos
                                    ? `https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photoreference=${place.photos[0].photo_reference}&key=${process.env.REACT_APP_GOOGLE_PLACES_API_KEY}`
                                    : "https://via.placeholder.com/400x300?text=No+Image"
                            }
                            alt={place.name}
                            className="place-img"
                        />
                        <div className="place-info">
                            <h2>{place.name}</h2>
                            <p>{place.vicinity}</p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default App;
