import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [places, setPlaces] = useState([]);
  const [location, setLocation] = useState("Rampally");
  const [loading, setLoading] = useState(false);

  // Fetch places whenever location changes
  useEffect(() => {
    const fetchPlaces = async () => {
      try {
        setLoading(true);
        const res = await axios.get("http://localhost:5000/api/places", {
          params: { location },
        });
        setPlaces(res.data);
        setLoading(false);
      } catch (err) {
        console.error("Failed to fetch places:", err);
        setLoading(false);
      }
    };

    fetchPlaces();
  }, [location]);

  const handleInputChange = (e) => {
    setLocation(e.target.value);
  };

  const handleSearch = () => {
    if (location.trim() !== "") {
      // trigger fetchPlaces by changing location
      setLocation(location);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold text-center mb-6">üìç Local Explorer</h1>

      {/* Search Bar */}
      <div className="flex justify-center mb-6">
        <input
          type="text"
          value={location}
          onChange={handleInputChange}
          placeholder="Enter a location (e.g., Rampally)"
          className="p-2 border rounded-l-md w-64"
        />
        <button
          onClick={handleSearch}
          className="bg-blue-600 text-white px-4 py-2 rounded-r-md hover:bg-blue-700"
        >
          Search
        </button>
      </div>

      {/* Loading State */}
      {loading && <p className="text-center text-gray-500">Loading places...</p>}

      {/* Places Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {places.map((place, index) => (
          <div
            key={index}
            className="bg-white rounded-lg shadow-md p-4 hover:shadow-lg transition"
          >
            <img
              src={
                place.photos && place.photos.length > 0
                  ? `https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photoreference=${place.photos[0].photo_reference}&key=AIzaSyB3PgI153B24mtSoJvKSjCPQyEyDQqu3K8`
                  : "https://via.placeholder.com/400x200?text=No+Image"
              }
              alt={place.name}
              className="w-full h-48 object-cover rounded-md mb-4"
            />
            <h2 className="text-xl font-semibold mb-2">{place.name}</h2>
            <p className="text-gray-600 mb-2">{place.formatted_address}</p>
            {place.rating && (
              <p className="text-yellow-500">‚≠ê {place.rating} / 5</p>
            )}
            <button
              onClick={() => {
                alert(`Reviews for ${place.name} will be shown here.`);
              }}
              className="mt-3 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
            >
              View Reviews
            </button>
          </div>
        ))}
      </div>

      {/* No Results */}
      {!loading && places.length === 0 && (
        <p className="text-center text-gray-500 mt-6">
          No places found for "{location}"
        </p>
      )}
    </div>
  );
}

export default App;
