import React, { useState } from "react";
import { GoogleMap, Marker, InfoWindow, useJsApiLoader } from "@react-google-maps/api";
import axios from "axios";

const API_KEY = "AIzaSyB3PgI153B24mtSoJvKSjCPQyEyDQqu3K8"; // Your API Key

const App = () => {
  const [places, setPlaces] = useState([]);
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [location, setLocation] = useState("Rampally");

  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: API_KEY,
    libraries: ["places"],
  });

  const fetchPlaces = async () => {
  try {
    const response = await axios.get(
      `http://localhost:5000/api/places?location=${location}`
    );
    console.log(response.data); // Check data in browser console
    setPlaces(response.data.results);
  } catch (error) {
    console.error("Error fetching places:", error);
  }
};

  return (
    <div className="h-screen flex">
      {/* Sidebar */}
      <div className="w-1/3 bg-gray-100 p-4 overflow-y-auto">
        <h2 className="text-xl font-bold mb-4">Places in {location}</h2>
        <input
          type="text"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          placeholder="Enter location..."
          className="p-2 border rounded w-full mb-2"
        />
        <button
          onClick={fetchPlaces}
          className="bg-blue-500 text-white px-4 py-2 rounded w-full"
        >
          Search
        </button>
        <ul className="mt-4">
          {places.map((place, index) => (
            <li
              key={index}
              className="p-2 border-b cursor-pointer hover:bg-gray-200"
              onClick={() => setSelectedPlace(place)}
            >
              <strong>{place.name}</strong><br />
              {place.formatted_address}<br />
              ‚≠ê {place.rating || "No rating"}
            </li>
          ))}
        </ul>
      </div>

      {/* Map */}
      <div className="w-2/3">
        {isLoaded ? (
          <GoogleMap
            center={{ lat: 17.498, lng: 78.623 }} // Default: Rampally approx coords
            zoom={13}
            mapContainerStyle={{ width: "100%", height: "100%" }}
          >
            {places.map((place, index) => (
              <Marker
                key={index}
                position={{
                  lat: place.geometry.location.lat,
                  lng: place.geometry.location.lng,
                }}
                onClick={() => setSelectedPlace(place)}
              />
            ))}

            {selectedPlace && (
              <InfoWindow
                position={{
                  lat: selectedPlace.geometry.location.lat,
                  lng: selectedPlace.geometry.location.lng,
                }}
                onCloseClick={() => setSelectedPlace(null)}
              >
                <div>
                  <h3 className="font-bold">{selectedPlace.name}</h3>
                  <p>{selectedPlace.formatted_address}</p>
                  <p>Rating: {selectedPlace.rating || "N/A"}</p>
                </div>
              </InfoWindow>
            )}
          </GoogleMap>
        ) : (
          <p>Loading Map...</p>
        )}
      </div>
    </div>
  );
};

export default App;
