import React, { useState } from "react";
import MapView from "./components/MapView";
import axios from "axios";

const App = () => {
  const [city, setCity] = useState("");
  const [formData, setFormData] = useState({
    name: "",
    type: "",
    city: "",
    lat: "",
    lng: ""
  });

  const handleSearch = (e) => {
    e.preventDefault();
    setCity(document.getElementById("cityInput").value.trim());
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleAddPlace = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/places", formData);
      alert(response.data.message);
      setFormData({ name: "", type: "", city: "", lat: "", lng: "" });
    } catch (err) {
      console.error("Error adding place:", err);
      alert("Failed to add place. Check console for details.");
    }
  };

  return (
    <div>
      {/* Search Form */}
      <form onSubmit={handleSearch} style={{ position: "absolute", top: 10, left: 10, zIndex: 1000 }}>
        <input
          id="cityInput"
          type="text"
          placeholder="Enter city name"
          style={{ padding: "8px", marginRight: "5px" }}
        />
        <button type="submit" style={{ padding: "8px" }}>Search</button>
      </form>

      {/* Add New Place Form */}
      <form onSubmit={handleAddPlace} style={{ position: "absolute", top: 60, left: 10, zIndex: 1000, background: "#fff", padding: "10px", borderRadius: "5px" }}>
        <h3>Add New Place</h3>
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={formData.name}
          onChange={handleInputChange}
          required
          style={{ display: "block", marginBottom: "5px", padding: "5px" }}
        />
        <input
          type="text"
          name="type"
          placeholder="Type (supermarket, school, etc.)"
          value={formData.type}
          onChange={handleInputChange}
          required
          style={{ display: "block", marginBottom: "5px", padding: "5px" }}
        />
        <input
          type="text"
          name="city"
          placeholder="City"
          value={formData.city}
          onChange={handleInputChange}
          required
          style={{ display: "block", marginBottom: "5px", padding: "5px" }}
        />
        <input
          type="number"
          name="lat"
          placeholder="Latitude"
          value={formData.lat}
          onChange={handleInputChange}
          required
          style={{ display: "block", marginBottom: "5px", padding: "5px" }}
        />
        <input
          type="number"
          name="lng"
          placeholder="Longitude"
          value={formData.lng}
          onChange={handleInputChange}
          required
          style={{ display: "block", marginBottom: "5px", padding: "5px" }}
        />
        <button type="submit" style={{ padding: "8px", marginTop: "5px" }}>Add Place</button>
      </form>

      <MapView city={city} />
    </div>
  );
};

export default App;
