import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

const FILTERS = ["all", "schools", "hospitals", "restaurants", "theaters", "medical shops"];

function App() {
    const [currentLocation, setCurrentLocation] = useState(null);
    const [places, setPlaces] = useState([]);
    const [filter, setFilter] = useState("all");

    useEffect(() => {
        navigator.geolocation.getCurrentPosition(
            ({ coords }) => setCurrentLocation({ lat: coords.latitude, lng: coords.longitude }),
            (err) => console.error("Geolocation error:", err)
        );
    }, []);

    useEffect(() => {
        if (currentLocation) {
            loadPlaces(currentLocation.lat, currentLocation.lng, filter);
        }
    }, [currentLocation, filter]);

    const loadPlaces = async (lat, lng, type) => {
        try {
            const res = await axios.get("http://localhost:5000/api/places", {
                params: { latitude: lat, longitude: lng, type }
            });
            setPlaces(res.data);
        } catch (err) {
            console.error("Error fetching places:", err);
        }
    };

    return (
        <div className="app">
            <header className="header">
                <span role="img" aria-label="globe" className="logo">üåç</span>
                <h1>Local Explorer</h1>
            </header>

            <input
                className="location-input"
                value={currentLocation ? `${currentLocation.lat}, ${currentLocation.lng}` : "Fetching location..."}
                disabled
            />

            <div className="filters">
                {FILTERS.map((f) => (
                    <button
                        key={f}
                        onClick={() => setFilter(f)}
                        className={filter === f ? "active" : ""}
                    >
                        {f.toUpperCase()}
                    </button>
                ))}
            </div>

            <div className="grid">
                {places.length === 0 && <p>No places found nearby.</p>}
                {places.map((p) => (
                    <div className="card" key={p.place_id}>
                        <img
                            src={
                                p.photos
                                    ? `https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photoreference=${p.photos[0].photo_reference}&key=YOUR_GOOGLE_PLACES_API_KEY`
                                    : "https://via.placeholder.com/400x300?text=No+Image"
                            }
                            alt={p.name}
                        />
                        <div className="card-info">
                            <h2>{p.name}</h2>
                            <p>{p.vicinity}</p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default App;
