import React, { useState } from "react";
import MapView from "./components/MapView";
import axios from "axios";

const App = () => {
  const [city, setCity] = useState("");
  const [typeFilter, setTypeFilter] = useState("All");
  const [formData, setFormData] = useState({
    name: "",
    type: "",
    city: "",
    lat: "",
    lng: ""
  });

  const handleSearch = (e) => {
    e.preventDefault();
    setCity(document.getElementById("cityInput").value.trim());
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleAddPlace = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/places", formData);
      alert(response.data.message);
      setFormData({ name: "", type: "", city: "", lat: "", lng: "" });
    } catch (err) {
      console.error("Error adding place:", err);
      alert("Failed to add place. Check console for details.");
    }
  };

  const handleFilterChange = (type) => {
    setTypeFilter(type);
  };

  return (
    <div className="relative">
      {/* Search Form */}
      <form onSubmit={handleSearch} className="absolute top-4 left-4 z-50 flex gap-2">
        <input
          id="cityInput"
          type="text"
          placeholder="Enter city name"
          className="p-2 border rounded w-60"
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Search
        </button>
      </form>

      {/* Filter Buttons */}
      <div className="absolute top-20 left-4 z-50 flex gap-2 bg-white p-2 rounded shadow">
        {["All", "Supermarket", "School", "Theater"].map((type) => (
          <button
            key={type}
            onClick={() => handleFilterChange(type)}
            className={`px-3 py-1 rounded ${typeFilter === type ? "bg-green-600 text-white" : "bg-gray-200"}`}
          >
            {type}
          </button>
        ))}
      </div>

      {/* Add New Place Form */}
      <form
        onSubmit={handleAddPlace}
        className="absolute top-36 left-4 z-50 bg-white p-4 rounded shadow w-80"
      >
        <h3 className="text-lg font-semibold mb-2">Add New Place</h3>
        {["name", "type", "city", "lat", "lng"].map((field) => (
          <input
            key={field}
            type={field === "lat" || field === "lng" ? "number" : "text"}
            name={field}
            placeholder={field.charAt(0).toUpperCase() + field.slice(1)}
            value={formData[field]}
            onChange={handleInputChange}
            required
            className="block w-full mb-2 p-2 border rounded"
          />
        ))}
        <button
          type="submit"
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 w-full"
        >
          Add Place
        </button>
      </form>

      <MapView city={city} filterType={typeFilter} />
    </div>
  );
};

export default App;
