import React, { useState, useEffect } from "react";
import axios from "axios";
import MapView from "./components/MapView";

export default function App() {
  const [location, setLocation] = useState("Rampally");
  const [places, setPlaces] = useState([]);
  const [selectedPlace, setSelectedPlace] = useState(null);

  const fetchPlaces = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/places", {
        params: { location },
      });
      setPlaces(res.data);
    } catch (err) {
      console.error("Failed to fetch places:", err);
    }
  };

  const fetchPlaceDetails = async (place_id) => {
    try {
      const res = await axios.get("http://localhost:5000/api/place-details", {
        params: { place_id },
      });
      setSelectedPlace(res.data);
    } catch (err) {
      console.error("Failed to fetch place details:", err);
    }
  };

  useEffect(() => {
    fetchPlaces();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    fetchPlaces();
  };

  return (
    <div className="p-4">
      <h1 className="text-3xl font-bold mb-4">Local Explorer</h1>
      <form onSubmit={handleSearch} className="mb-4">
        <input
          type="text"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          placeholder="Enter location"
          className="border rounded px-2 py-1 mr-2"
        />
        <button type="submit" className="bg-blue-500 text-white px-3 py-1 rounded">
          Search
        </button>
      </form>

      <div className="flex">
        <div className="w-1/2">
          <MapView
            places={places}
            onMarkerClick={(place) => fetchPlaceDetails(place.place_id)}
          />
        </div>
        <div className="w-1/2 p-4">
          <h2 className="text-xl font-semibold mb-2">Places</h2>
          {places.map((place) => (
            <div
              key={place.place_id}
              className="border rounded p-2 mb-2 cursor-pointer hover:bg-gray-100"
              onClick={() => fetchPlaceDetails(place.place_id)}
            >
              <img
                src={place.photo_url}
                alt={place.name}
                className="w-full h-40 object-cover rounded mb-2"
              />
              <h3 className="text-lg font-bold">{place.name}</h3>
              <p>{place.formatted_address}</p>
              <p>Rating: {place.rating ?? "N/A"}</p>
            </div>
          ))}

          {selectedPlace && (
            <div className="mt-4">
              <h2 className="text-2xl font-bold">{selectedPlace.name}</h2>
              <p>{selectedPlace.formatted_address}</p>
              <h3 className="text-xl font-semibold mt-2">Reviews:</h3>
              {selectedPlace.reviews ? (
                selectedPlace.reviews.map((review, idx) => (
                  <div key={idx} className="border-t pt-2 mt-2">
                    <p className="italic">"{review.text}"</p>
                    <p>— {review.author_name}</p>
                  </div>
                ))
              ) : (
                <p>No reviews available.</p>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
