import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [location, setLocation] = useState("");
  const [places, setPlaces] = useState({});
  const [loading, setLoading] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState("all");

  const fetchPlaces = async () => {
    if (!location) return alert("Please enter a location");
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:5000/api/places?location=${location}`
      );
      setPlaces(response.data);
    } catch (error) {
      console.error("Error fetching places:", error);
      alert("Failed to fetch places.");
    }
    setLoading(false);
  };

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          try {
            const geoResponse = await axios.get(
              `https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&key=AIzaSyDki4k3wRgR7TEw1ugkkaWqe1tBxxnDfDk`
            );
            const detectedLocation = geoResponse.data.results[0].formatted_address;
            setLocation(detectedLocation);
            fetchPlaces();
          } catch (error) {
            console.error("Error getting location:", error);
          }
        },
        (err) => {
          console.error("Geolocation permission denied:", err);
        }
      );
    }
    // eslint-disable-next-line
  }, []);

  const categories = ["schools", "colleges", "theaters", "medical shops"];
  const categoryIcons = {
    schools: "üè´",
    colleges: "üéì",
    theaters: "üé¨",
    "medical shops": "üíä",
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-blue-100 p-4">
      <h1 className="text-4xl font-bold text-center mb-8 text-blue-700">
        üåé Local Explorer
      </h1>

      {/* Search Bar */}
      <div className="flex justify-center mb-6">
        <input
          type="text"
          placeholder="Enter location (e.g., Hyderabad)"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="border border-blue-300 rounded-l px-4 py-2 w-1/2 shadow"
        />
        <button
          onClick={fetchPlaces}
          className="bg-blue-600 text-white px-5 py-2 rounded-r shadow hover:bg-blue-700"
        >
          Search
        </button>
      </div>

      {/* Filter Buttons */}
      <div className="flex justify-center mb-8 flex-wrap gap-2">
        <button
          onClick={() => setSelectedCategory("all")}
          className={`px-4 py-2 rounded-full ${
            selectedCategory === "all"
              ? "bg-blue-600 text-white"
              : "bg-white text-blue-700 border border-blue-300"
          }`}
        >
          üåü All
        </button>
        {categories.map((cat) => (
          <button
            key={cat}
            onClick={() => setSelectedCategory(cat)}
            className={`px-4 py-2 rounded-full capitalize ${
              selectedCategory === cat
                ? "bg-blue-600 text-white"
                : "bg-white text-blue-700 border border-blue-300"
            }`}
          >
            {categoryIcons[cat]} {cat}
          </button>
        ))}
      </div>

      {/* Loading */}
      {loading && <p className="text-center text-lg text-blue-700">Loading places...</p>}

      {/* Places Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {Object.entries(places)
          .filter(
            ([category]) => selectedCategory === "all" || selectedCategory === category
          )
          .map(([category, placesArray]) =>
            placesArray.map((place, index) => (
              <div
                key={index}
                className="bg-white rounded-xl shadow-lg hover:shadow-2xl transition duration-300 overflow-hidden"
              >
                <img
                  src={place.photo_url}
                  alt={place.name}
                  className="w-full h-48 object-cover"
                />
                <div className="p-4">
                  <h3 className="text-lg font-bold text-gray-800">{place.name}</h3>
                  <p className="text-gray-600 text-sm mb-2">
                    {place.formatted_address}
                  </p>
                  {place.rating && (
                    <p className="text-yellow-500 text-sm">‚≠ê {place.rating}</p>
                  )}
                </div>
              </div>
            ))
          )}
      </div>
    </div>
  );
}

export default App;
