import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  TextField,
  Card,
  CardContent,
  Typography,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  Button,
  Grid,
  ToggleButton,
  ToggleButtonGroup,
} from "@mui/material";
import { Loader } from "@googlemaps/js-api-loader";
import { motion } from "framer-motion";

const App = () => {
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [filterType, setFilterType] = useState("all");
  const [currentLocation, setCurrentLocation] = useState("");

  const handleCardClick = (place) => {
    setSelectedPlace(place);
  };

  const handleClose = () => {
    setSelectedPlace(null);
  };

  const handleFilterChange = (_, newFilter) => {
    if (newFilter !== null) {
      setFilterType(newFilter);
    }
  };

  // Load Google Maps Autocomplete once
  useEffect(() => {
    const loader = new Loader({
      apiKey: "AIzaSyB3PgI153B24mtSoJvKSjCPQyEyDQqu3K8",
      libraries: ["places"],
    });

    loader.load().then(() => {
      const input = document.getElementById("locationInput");
      const autocomplete = new window.google.maps.places.Autocomplete(input);
      autocomplete.addListener("place_changed", () => {
        const place = autocomplete.getPlace();
        if (place.formatted_address) {
          setCurrentLocation(place.formatted_address);
        }
      });
    });
  }, []);

  // Fetch places whenever currentLocation or filterType changes
  useEffect(() => {
    if (!currentLocation) return;

    const fetchPlaces = async () => {
      setLoading(true);
      try {
        const response = await axios.get(
          `http://localhost:5000/api/places?location=${currentLocation}&type=${filterType}`
        );
        setPlaces(response.data.results || []);
      } catch (error) {
        console.error("Error fetching places:", error);
        setPlaces([]);
      }
      setLoading(false);
    };

    fetchPlaces();
  }, [currentLocation, filterType]);

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "#f7f9fc",
        padding: "2rem",
        fontFamily: "Arial, sans-serif",
      }}
    >
      <Typography
        variant="h3"
        align="center"
        style={{
          marginBottom: "1rem",
          color: "#1e88e5",
          fontWeight: "bold",
        }}
      >
        üåç Local Explorer
      </Typography>

      <div
        style={{
          display: "flex",
          justifyContent: "center",
          marginBottom: "1rem",
        }}
      >
        <TextField
          id="locationInput"
          placeholder="Search a location e.g. Rampally"
          variant="outlined"
          fullWidth
          style={{
            maxWidth: 600,
            backgroundColor: "#fff",
            borderRadius: 8,
            boxShadow: "0 2px 8px rgba(0, 0, 0, 0.1)",
          }}
        />
      </div>

      <div
        style={{
          display: "flex",
          justifyContent: "center",
          marginBottom: "2rem",
        }}
      >
        <ToggleButtonGroup
          value={filterType}
          exclusive
          onChange={handleFilterChange}
          aria-label="filter"
        >
          <ToggleButton value="all">All</ToggleButton>
          <ToggleButton value="school">Schools</ToggleButton>
          <ToggleButton value="hospital">Hospitals</ToggleButton>
          <ToggleButton value="restaurant">Restaurants</ToggleButton>
          <ToggleButton value="theater">Theaters</ToggleButton>
          <ToggleButton value="pharmacy">Medical Shops</ToggleButton>
        </ToggleButtonGroup>
      </div>

      {loading ? (
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "3rem",
          }}
        >
          <CircularProgress />
        </div>
      ) : (
        <Grid container spacing={3}>
          {places.length === 0 ? (
            <Typography
              variant="h6"
              align="center"
              style={{ width: "100%", color: "#888" }}
            >
              No places found for this filter/location.
            </Typography>
          ) : (
            places.map((place, idx) => (
              <Grid item xs={12} sm={6} md={4} key={idx}>
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: idx * 0.05 }}
                >
                  <Card
                    style={{
                      borderRadius: 16,
                      boxShadow: "0 8px 30px rgba(0,0,0,0.15)",
                      cursor: "pointer",
                      background: "#fff",
                      overflow: "hidden",
                    }}
                    onClick={() => handleCardClick(place)}
                  >
                    <img
                      src={
                        place.photos
                          ? `https://maps.googleapis.com/maps/api/place/photo?maxwidth=800&photoreference=${place.photos[0].photo_reference}&key=AIzaSyB3PgI153B24mtSoJvKSjCPQyEyDQqu3K8`
                          : "https://via.placeholder.com/800x400?text=No+Image+Available"
                      }
                      alt={place.name}
                      style={{
                        width: "100%",
                        height: 220,
                        objectFit: "cover",
                      }}
                      onError={(e) => {
                        e.target.src =
                          "https://via.placeholder.com/800x400?text=No+Image+Available";
                      }}
                    />
                    <CardContent>
                      <Typography
                        variant="h6"
                        style={{ fontWeight: "600", color: "#333" }}
                      >
                        {place.name}
                      </Typography>
                      <Typography
                        variant="body2"
                        color="textSecondary"
                        style={{ marginTop: 4 }}
                      >
                        {place.formatted_address}
                      </Typography>
                    </CardContent>
                  </Card>
                </motion.div>
              </Grid>
            ))
          )}
        </Grid>
      )}

      <Dialog open={!!selectedPlace} onClose={handleClose} fullWidth maxWidth="sm">
        <DialogTitle>{selectedPlace?.name}</DialogTitle>
        <DialogContent>
          <Typography variant="body1" gutterBottom>
            Address: {selectedPlace?.formatted_address}
          </Typography>
          <Typography
            variant="body2"
            color="textSecondary"
            gutterBottom
            style={{ marginTop: "1rem" }}
          >
            Showing reviews (if available):
          </Typography>
          <ul>
            {selectedPlace?.reviews
              ? selectedPlace.reviews.map((review, index) => (
                  <li key={index}>
                    <strong>{review.author_name}:</strong> {review.text}
                  </li>
                ))
              : "No reviews available."}
          </ul>
        </DialogContent>
        <Button
          onClick={handleClose}
          style={{ margin: "1rem" }}
          variant="contained"
          color="primary"
        >
          Close
        </Button>
      </Dialog>
    </div>
  );
};

export default App;
