import React, { useState } from "react";
import axios from "axios";

function App() {
  const [location, setLocation] = useState("");
  const [places, setPlaces] = useState({});
  const [loading, setLoading] = useState(false);

  const fetchPlaces = async () => {
    if (!location) return alert("Please enter a location");
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:5000/api/places?location=${location}`
      );
      console.log(response.data); // See API response in console
      setPlaces(response.data);
    } catch (error) {
      console.error("Error fetching places:", error);
      alert("Failed to fetch places. Check console for details.");
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <h1 className="text-3xl font-bold text-center mb-6">
        üó∫Ô∏è Local Explorer App
      </h1>

      <div className="flex justify-center mb-6">
        <input
          type="text"
          placeholder="Enter location (e.g., Rampally)"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="border border-gray-300 rounded-l px-4 py-2 w-1/3"
        />
        <button
          onClick={fetchPlaces}
          className="bg-blue-500 text-white px-4 py-2 rounded-r hover:bg-blue-600"
        >
          Search
        </button>
      </div>

      {loading && <p className="text-center">Loading places...</p>}

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {Object.entries(places).map(([category, placesArray]) => (
          <div key={category} className="bg-white p-4 rounded shadow">
            <h2 className="text-2xl font-semibold mb-4 capitalize">
              {category}
            </h2>
            {placesArray.length === 0 ? (
              <p className="text-gray-500">No {category} found.</p>
            ) : (
              placesArray.map((place, index) => (
                <div
                  key={index}
                  className="mb-3 p-3 border-b last:border-b-0"
                >
                  <h3 className="font-bold">{place.name}</h3>
                  <p className="text-sm text-gray-600">
                    {place.formatted_address}
                  </p>
                  {place.rating && (
                    <p className="text-yellow-500">‚≠ê {place.rating}</p>
                  )}
                </div>
              ))
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
