import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [location, setLocation] = useState("");
  const [places, setPlaces] = useState({});
  const [loading, setLoading] = useState(false);
  const [activeCategory, setActiveCategory] = useState("schools");
  const [selectedPlace, setSelectedPlace] = useState(null);

  const fetchPlaces = async (loc) => {
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:5000/api/places?location=${loc}`
      );
      setPlaces(response.data);
    } catch (error) {
      console.error("Error fetching places:", error);
      alert("Failed to fetch places.");
    }
    setLoading(false);
  };

  const detectLocationAndFetch = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const lat = position.coords.latitude;
          const lng = position.coords.longitude;
          try {
            const res = await axios.get(
              `https://maps.googleapis.com/maps/api/geocode/json?latlng=${lat},${lng}&key=AIzaSyDki4k3wRgR7TEw1ugkkaWqe1tBxxnDfDk`
            );
            const city = res.data.results[0].address_components.find((c) =>
              c.types.includes("locality")
            )?.long_name;
            if (city) {
              setLocation(city);
              fetchPlaces(city);
            } else {
              alert("Unable to detect city.");
            }
          } catch (err) {
            console.error("Geocoding error:", err);
            alert("Failed to detect location.");
          }
        },
        (error) => {
          console.error("Geolocation error:", error);
          alert("Please enable location permission.");
        }
      );
    } else {
      alert("Geolocation not supported.");
    }
  };

  useEffect(() => {
    detectLocationAndFetch();
  }, []);

  const fetchPlaceDetails = async (placeId) => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/place-details?place_id=${placeId}`
      );
      setSelectedPlace(response.data);
    } catch (error) {
      console.error("Error fetching place details:", error);
      alert("Failed to fetch place details.");
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <h1 className="text-3xl font-bold text-center mb-6">
        üó∫Ô∏è Local Explorer App
      </h1>

      <div className="flex justify-center mb-4 space-x-2">
        {["schools", "colleges", "theaters", "medical shops"].map((cat) => (
          <button
            key={cat}
            onClick={() => setActiveCategory(cat)}
            className={`px-4 py-2 rounded-full ${
              activeCategory === cat
                ? "bg-blue-600 text-white"
                : "bg-gray-200 text-gray-800"
            }`}
          >
            {cat.charAt(0).toUpperCase() + cat.slice(1)}
          </button>
        ))}
      </div>

      <div className="flex justify-center mb-6">
        <input
          type="text"
          placeholder="Enter location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="border border-gray-300 rounded-l px-4 py-2 w-1/3"
        />
        <button
          onClick={() => fetchPlaces(location)}
          className="bg-blue-500 text-white px-4 py-2 rounded-r hover:bg-blue-600"
        >
          Search
        </button>
      </div>

      {loading ? (
        <p className="text-center">Loading places...</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {places[activeCategory]?.map((place, index) => (
            <div
              key={index}
              className="bg-white rounded-xl shadow hover:shadow-lg cursor-pointer"
              onClick={() => fetchPlaceDetails(place.place_id)}
            >
              <img
                src={place.photo_url}
                alt={place.name}
                className="w-full h-48 object-cover rounded-t-xl"
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = `https://source.unsplash.com/400x300/?${activeCategory}`;
                }}
              />
              <div className="p-4">
                <h3 className="text-lg font-bold">{place.name}</h3>
                <p className="text-gray-600">{place.formatted_address}</p>
                {place.rating && (
                  <p className="text-yellow-500">‚≠ê {place.rating}</p>
                )}
              </div>
            </div>
          ))}
        </div>
      )}

      {selectedPlace && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center"
          onClick={() => setSelectedPlace(null)}
        >
          <div
            className="bg-white rounded-lg shadow p-6 w-11/12 md:w-2/3 lg:w-1/2"
            onClick={(e) => e.stopPropagation()}
          >
            <h2 className="text-2xl font-bold mb-2">{selectedPlace.name}</h2>
            <p className="mb-2">{selectedPlace.formatted_address}</p>
            {selectedPlace.rating && (
              <p className="text-yellow-500 mb-2">‚≠ê {selectedPlace.rating}</p>
            )}
            <h3 className="text-xl font-semibold mb-2">Customer Reviews:</h3>
            {selectedPlace.reviews ? (
              selectedPlace.reviews.map((review, idx) => (
                <div key={idx} className="mb-3 border-b pb-2">
                  <p className="font-bold">{review.author_name}</p>
                  <p className="text-yellow-500">‚≠ê {review.rating}</p>
                  <p className="text-gray-700">{review.text}</p>
                </div>
              ))
            ) : (
              <p className="text-gray-500">No reviews available.</p>
            )}
            <button
              className="mt-4 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              onClick={() => setSelectedPlace(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
