import React, { useState } from "react";
import axios from "axios";

function App() {
  const [location, setLocation] = useState("");
  const [places, setPlaces] = useState({});
  const [loading, setLoading] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [selectedPlace, setSelectedPlace] = useState(null);

  const categories = ["all", "schools", "colleges", "theaters", "medical shops"];

  const fetchPlaces = async (loc = location) => {
    if (!loc) return alert("Please enter a location");
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/places?location=${loc}`);
      setPlaces(response.data);
      setSelectedCategory("all"); // Show all places by default
    } catch (error) {
      console.error("Error fetching places:", error);
      alert("Failed to fetch places.");
    }
    setLoading(false);
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
  };

  const fetchPlaceDetails = async (placeId) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/place-details?place_id=${placeId}`);
      setSelectedPlace(response.data);
    } catch (error) {
      console.error("Error fetching place details:", error);
      alert("Failed to fetch place details.");
    }
  };

  // Get places to display based on selected category
  const getPlacesToDisplay = () => {
    if (selectedCategory === "all") {
      // Combine all places from all categories
      return Object.values(places).flat();
    }
    return places[selectedCategory] || [];
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-gradient-to-r from-orange-500 to-red-500 text-white p-6 text-center">
        <h1 className="text-3xl font-bold">Local Explorer</h1>
        <p className="mt-1">Discover Schools, Colleges, Theaters & Medical Shops Nearby</p>
      </header>

      <div className="flex justify-center mt-4 space-x-2">
        {categories.map((cat) => (
          <button
            key={cat}
            onClick={() => handleCategoryClick(cat)}
            className={`px-4 py-2 rounded-full text-white ${
              selectedCategory === cat ? "bg-red-600" : "bg-orange-400"
            } hover:bg-red-500`}
          >
            {cat.charAt(0).toUpperCase() + cat.slice(1)}
          </button>
        ))}
      </div>

      <div className="flex justify-center my-6">
        <input
          type="text"
          placeholder="Enter location (e.g., Hyderabad)"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && fetchPlaces()}
          className="border border-gray-300 rounded-l px-4 py-2 w-1/3"
        />
        <button
          onClick={() => fetchPlaces()}
          className="bg-red-500 text-white px-4 py-2 rounded-r hover:bg-red-600"
        >
          Search
        </button>
      </div>

      {loading && <p className="text-center text-gray-600">Loading...</p>}

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 p-4">
        {getPlacesToDisplay().map((place, index) => (
          <div
            key={index}
            onClick={() => fetchPlaceDetails(place.place_id)}
            className="bg-white rounded-lg shadow-md p-4 hover:shadow-lg transition duration-300 cursor-pointer border border-gray-200"
          >
            <div className="flex items-center mb-3">
              <div className="bg-gray-100 p-2 rounded-full mr-3">
                {place.category === "schools" && "üè´"}
                {place.category === "colleges" && "üéì"}
                {place.category === "theaters" && "üé¨"}
                {place.category === "medical shops" && "üíä"}
              </div>
              <div>
                <h3 className="text-lg font-semibold">{place.name}</h3>
                <p className="text-gray-500 text-sm">{place.formatted_address}</p>
              </div>
            </div>
            {place.rating && (
              <div className="flex items-center text-yellow-500">
                <span>‚≠ê</span>
                <span className="ml-1">{place.rating}</span>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Modal */}
      {selectedPlace && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50"
          onClick={() => setSelectedPlace(null)}
        >
          <div
            className="bg-white rounded-lg shadow-lg p-6 w-11/12 md:w-2/3 lg:w-1/2"
            onClick={(e) => e.stopPropagation()}
          >
            <h2 className="text-2xl font-bold mb-2">{selectedPlace.name}</h2>
            <p>{selectedPlace.formatted_address}</p>
            {selectedPlace.rating && (
              <p className="text-yellow-500 mb-2">‚≠ê {selectedPlace.rating}</p>
            )}
            <button
              className="mt-4 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              onClick={() => setSelectedPlace(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
