import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [location, setLocation] = useState("");
  const [places, setPlaces] = useState({});
  const [loading, setLoading] = useState(false);
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [activeCategory, setActiveCategory] = useState("all");

  // Auto-detect user's location
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          try {
            const res = await axios.get(
              `https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&key=AIzaSyDki4k3wRgR7TEw1ugkkaWqe1tBxxnDfDk`
            );
            const detectedLocation =
              res.data.results[0]?.address_components[2]?.long_name;
            setLocation(detectedLocation || "");
          } catch (err) {
            console.error("Error detecting location:", err);
          }
        },
        (error) => {
          console.error("Geolocation error:", error);
        }
      );
    }
  }, []);

  const fetchPlaces = async () => {
    if (!location) return alert("Please enter a location");
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:5000/api/places?location=${location}`
      );
      setPlaces(response.data);
    } catch (error) {
      console.error("Error fetching places:", error);
      alert("Failed to fetch places.");
    }
    setLoading(false);
  };

  const fetchPlaceDetails = async (placeId) => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/place-details?place_id=${placeId}`
      );
      setSelectedPlace(response.data);
    } catch (error) {
      console.error("Error fetching place details:", error);
      alert("Failed to fetch place details.");
    }
  };

  useEffect(() => {
    if (location) fetchPlaces();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [location]);

  const categories = ["schools", "hospitals", "theaters", "medical shops"];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-100 p-6">
      <h1 className="text-4xl font-bold text-center text-blue-800 mb-8">
        🗺️ Local Explorer
      </h1>

      {/* Category Buttons */}
      <div className="flex justify-center gap-4 mb-6 flex-wrap">
        <button
          onClick={() => setActiveCategory("all")}
          className={`px-4 py-2 rounded-full ${
            activeCategory === "all"
              ? "bg-blue-600 text-white"
              : "bg-white border border-blue-600 text-blue-600"
          } hover:bg-blue-500 hover:text-white transition`}
        >
          All
        </button>
        {categories.map((category) => (
          <button
            key={category}
            onClick={() => setActiveCategory(category)}
            className={`px-4 py-2 rounded-full capitalize ${
              activeCategory === category
                ? "bg-blue-600 text-white"
                : "bg-white border border-blue-600 text-blue-600"
            } hover:bg-blue-500 hover:text-white transition`}
          >
            {category}
          </button>
        ))}
      </div>

      {/* Search Bar */}
      <div className="flex justify-center mb-8">
        <input
          type="text"
          placeholder="Enter location (e.g., Rampally)"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="border border-gray-300 rounded-l px-4 py-2 w-1/2"
        />
        <button
          onClick={fetchPlaces}
          className="bg-green-500 text-white px-4 py-2 rounded-r hover:bg-green-600 transition"
        >
          Search
        </button>
      </div>

      {loading && (
        <p className="text-center text-blue-600 font-medium">Loading places...</p>
      )}

      {/* Places Grid */}
      {Object.entries(places)
        .filter(
          ([category]) =>
            activeCategory === "all" || category === activeCategory
        )
        .map(([category, placesArray]) => (
          <div key={category} className="mb-8">
            <h2 className="text-2xl font-semibold text-blue-700 mb-4 capitalize">
              {category}
            </h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
              {placesArray.map((place, index) => (
                <div
                  key={index}
                  className="bg-white rounded-xl shadow hover:shadow-xl transition cursor-pointer"
                  onClick={() => fetchPlaceDetails(place.place_id)}
                >
                  <img
                    src={
                      place.photo_url ||
                      "https://source.unsplash.com/400x300/?city,place"
                    }
                    alt={place.name}
                    className="w-full h-48 object-cover rounded-t-xl"
                    onError={(e) =>
                      (e.target.src =
                        "https://source.unsplash.com/400x300/?city,place")
                    }
                  />
                  <div className="p-4">
                    <h3 className="text-lg font-bold text-gray-800">
                      {place.name}
                    </h3>
                    <p className="text-gray-600">{place.formatted_address}</p>
                    {place.rating && (
                      <p className="text-yellow-500 mt-2">⭐ {place.rating}</p>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}

      {/* Modal for Place Details */}
      {selectedPlace && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50"
          onClick={() => setSelectedPlace(null)}
        >
          <div
            className="bg-white rounded-xl shadow-lg p-6 w-11/12 md:w-2/3 lg:w-1/2"
            onClick={(e) => e.stopPropagation()}
          >
            <h2 className="text-2xl font-bold mb-2">{selectedPlace.name}</h2>
            <p className="mb-2">{selectedPlace.formatted_address}</p>
            {selectedPlace.rating && (
              <p className="text-yellow-500 mb-2">⭐ {selectedPlace.rating}</p>
            )}

            <h3 className="text-xl font-semibold mb-2">Customer Reviews:</h3>
            {selectedPlace.reviews ? (
              selectedPlace.reviews.map((review, idx) => (
                <div key={idx} className="mb-3 border-b pb-2">
                  <p className="font-bold">{review.author_name}</p>
                  <p className="text-yellow-500">⭐ {review.rating}</p>
                  <p className="text-gray-700">{review.text}</p>
                </div>
              ))
            ) : (
              <p className="text-gray-500">No reviews available.</p>
            )}

            <button
              className="mt-4 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              onClick={() => setSelectedPlace(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
