import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  const [places, setPlaces] = useState([]);
  const [filteredPlaces, setFilteredPlaces] = useState([]);
  const [currentLocation, setCurrentLocation] = useState("");
  const [filterType, setFilterType] = useState("all");

  // Fetch places from backend
  const fetchPlaces = async (location) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/places?location=${location}`);
      // ‚úÖ Fix: Your API already returns an array (not response.data.results)
      setPlaces(response.data);
      filterPlaces(response.data, filterType);
    } catch (error) {
      console.error("Error fetching places:", error);
      setPlaces([]);
      setFilteredPlaces([]);
    }
  };

  // Filter places by type
  const filterPlaces = (placesList, type) => {
    if (type === "all") {
      setFilteredPlaces(placesList);
    } else {
      const filtered = placesList.filter((place) =>
        place.types.includes(type.toLowerCase())
      );
      setFilteredPlaces(filtered);
    }
  };

  // Handle search input change
  const handleLocationChange = (e) => {
    setCurrentLocation(e.target.value);
  };

  // Handle filter button click
  const handleFilterChange = (type) => {
    setFilterType(type);
    filterPlaces(places, type);
  };

  // Fetch places whenever currentLocation changes
  useEffect(() => {
    if (currentLocation.trim() !== "") {
      fetchPlaces(currentLocation.trim());
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [currentLocation]);

  return (
    <div className="App">
      <h1>
        <span role="img" aria-label="globe">
          üåç
        </span>{" "}
        Local Explorer
      </h1>

      <input
        type="text"
        placeholder="Search a location e.g. Rampally"
        value={currentLocation}
        onChange={handleLocationChange}
      />

      <div className="filters">
        {["all", "schools", "hospitals", "restaurants", "theaters", "medical shops"].map((type) => (
          <button
            key={type}
            className={filterType === type ? "active" : ""}
            onClick={() => handleFilterChange(type)}
          >
            {type.toUpperCase()}
          </button>
        ))}
      </div>

      <div className="places">
        {filteredPlaces.length > 0 ? (
          filteredPlaces.map((place, index) => (
            <div key={index} className="place-card">
              <h2>{place.name}</h2>
              <p>{place.formatted_address}</p>
              {place.rating && <p>‚≠ê Rating: {place.rating}</p>}
              {place.opening_hours && place.opening_hours.open_now !== undefined && (
                <p>{place.opening_hours.open_now ? "üü¢ Open Now" : "üî¥ Closed"}</p>
              )}
            </div>
          ))
        ) : (
          <p>No places found for this filter/location.</p>
        )}
      </div>
    </div>
  );
};

export default App;
