import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";

function App() {
  const [location, setLocation] = useState("");
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [activeCategory, setActiveCategory] = useState("schools");

  const categories = ["schools", "colleges", "theaters", "medical shops"];

  const fetchPlaces = useCallback(
    async (loc = location, category = activeCategory) => {
      if (!loc) return alert("Please enter a location");
      setLoading(true);
      try {
        const response = await axios.get(
          `http://localhost:5000/api/places?location=${loc}&category=${category}`
        );
        setPlaces(response.data[category] || []);
      } catch (error) {
        console.error("Error fetching places:", error);
        alert("Failed to fetch places.");
      }
      setLoading(false);
    },
    [location, activeCategory]
  );

  useEffect(() => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          try {
            const response = await axios.get(
              `https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&key=AIzaSyDki4k3wRgR7TEw1ugkkaWqe1tBxxnDfDk`
            );
            const userLocation =
              response.data.results[0]?.formatted_address || "";
            setLocation(userLocation);
            fetchPlaces(userLocation, activeCategory);
          } catch (err) {
            console.error("Error getting location:", err);
          }
        },
        (error) => {
          console.error("Geolocation error:", error);
          alert("Location permission denied. Please enter manually.");
        }
      );
    } else {
      alert("Geolocation not supported. Please enter location manually.");
    }
  }, [fetchPlaces, activeCategory]);

  const fetchPlaceDetails = async (placeId) => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/place-details?place_id=${placeId}`
      );
      setSelectedPlace(response.data);
    } catch (error) {
      console.error("Error fetching place details:", error);
      alert("Failed to fetch place details.");
    }
  };

  const handleCategoryClick = (category) => {
    setActiveCategory(category);
    fetchPlaces(location, category);
  };

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      <h1 className="text-4xl font-bold text-center mb-6 text-blue-700">
        üó∫Ô∏è Local Explorer
      </h1>

      {/* Category Buttons */}
      <div className="flex flex-wrap justify-center gap-3 mb-6">
        {categories.map((cat) => (
          <button
            key={cat}
            onClick={() => handleCategoryClick(cat)}
            className={`px-4 py-2 rounded-full ${
              activeCategory === cat
                ? "bg-blue-600 text-white"
                : "bg-gray-200 text-gray-700"
            } hover:bg-blue-500 hover:text-white transition`}
          >
            {cat.charAt(0).toUpperCase() + cat.slice(1)}
          </button>
        ))}
      </div>

      {/* Search Bar */}
      <div className="flex justify-center mb-6">
        <input
          type="text"
          placeholder="Enter location (e.g., Rampally)"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="border border-gray-300 rounded-l px-4 py-2 w-1/3"
        />
        <button
          onClick={() => fetchPlaces()}
          className="bg-green-500 text-white px-4 py-2 rounded-r hover:bg-green-600"
        >
          Search
        </button>
      </div>

      {loading && <p className="text-center text-lg">Loading places...</p>}

      {/* Places Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {places.map((place, index) => (
          <div
            key={index}
            className="bg-white rounded-xl shadow hover:shadow-lg transition cursor-pointer"
            onClick={() => fetchPlaceDetails(place.place_id)}
          >
            <img
              src={place.photo_url || "https://via.placeholder.com/400x300?text=No+Image"}
              alt={place.name}
              className="w-full h-48 object-cover rounded-t-xl"
              onError={(e) => {
                if (
                  e.target.src !==
                  "https://via.placeholder.com/400x300?text=No+Image"
                ) {
                  e.target.src =
                    "https://via.placeholder.com/400x300?text=No+Image";
                }
              }}
            />
            <div className="p-4">
              <h3 className="text-lg font-bold">{place.name}</h3>
              <p className="text-gray-600">{place.formatted_address}</p>
              {place.rating && (
                <p className="text-yellow-500">‚≠ê {place.rating}</p>
              )}
            </div>
          </div>
        ))}
      </div>

      {/* Place Details Modal */}
      {selectedPlace && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50"
          onClick={() => setSelectedPlace(null)}
        >
          <div
            className="bg-white rounded-xl shadow-lg p-6 w-11/12 md:w-2/3 lg:w-1/2"
            onClick={(e) => e.stopPropagation()}
          >
            <h2 className="text-3xl font-bold mb-2">{selectedPlace.name}</h2>
            <p className="mb-2">{selectedPlace.formatted_address}</p>
            {selectedPlace.rating && (
              <p className="text-yellow-500 mb-2">‚≠ê {selectedPlace.rating}</p>
            )}
            <h3 className="text-xl font-semibold mb-2">Customer Reviews:</h3>
            {selectedPlace.reviews ? (
              selectedPlace.reviews.map((review, idx) => (
                <div key={idx} className="mb-3 border-b pb-2">
                  <p className="font-bold">{review.author_name}</p>
                  <p className="text-yellow-500">‚≠ê {review.rating}</p>
                  <p className="text-gray-700">{review.text}</p>
                </div>
              ))
            ) : (
              <p className="text-gray-500">No reviews available.</p>
            )}
            <button
              className="mt-4 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              onClick={() => setSelectedPlace(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
