import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [location, setLocation] = useState("");
  const [places, setPlaces] = useState({});
  const [loading, setLoading] = useState(false);
  const [selectedPlace, setSelectedPlace] = useState(null);

  const fetchPlaces = async () => {
    if (!location) return alert("Please enter a location");
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:5000/api/places?location=${location}`
      );
      setPlaces(response.data);
    } catch (error) {
      console.error("Error fetching places:", error);
      alert("Failed to fetch places.");
    }
    setLoading(false);
  };

  const fetchPlaceDetails = async (placeId) => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/place-details?place_id=${placeId}`
      );
      setSelectedPlace(response.data);
    } catch (error) {
      console.error("Error fetching place details:", error);
      alert("Failed to fetch place details.");
    }
  };

  const detectLocationAndFetch = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          try {
            const response = await axios.get(
              `https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&key=AIzaSyDki4k3wRgR7TEw1ugkkaWqe1tBxxnDfDk`
            );
            const userLocation = response.data.results[0].formatted_address;
            setLocation(userLocation);
            fetchPlaces();
          } catch (error) {
            console.error("Error fetching geolocation:", error);
            alert("Could not fetch your location.");
          }
        },
        (error) => {
          console.error("Geolocation error:", error);
          alert("Location access denied.");
        }
      );
    } else {
      alert("Geolocation is not supported by this browser.");
    }
  };

  useEffect(() => {
    detectLocationAndFetch();
    // eslint-disable-next-line
  }, []);

  const categoryIcons = {
    schools: "🎓",
    colleges: "🏫",
    theaters: "🎬",
    "medical shops": "💊"
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <h1 className="text-3xl font-bold text-center mb-6">🗺️ Local Explorer</h1>

      <div className="flex justify-center mb-6">
        <input
          type="text"
          placeholder="Enter location (e.g., Hyderabad)"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="border border-gray-300 rounded-l px-4 py-2 w-1/3"
        />
        <button
          onClick={fetchPlaces}
          className="bg-blue-500 text-white px-4 py-2 rounded-r hover:bg-blue-600"
        >
          Search
        </button>
      </div>

      {loading && <p className="text-center">Loading places...</p>}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {Object.entries(places).map(([category, placesArray]) => (
          <div key={category}>
            <h2 className="text-2xl font-semibold mb-4 capitalize">
              {categoryIcons[category]} {category}
            </h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              {placesArray.map((place, index) => (
                <div
                  key={index}
                  className="bg-white rounded-xl shadow-md hover:shadow-xl transition p-2 cursor-pointer"
                  onClick={() => fetchPlaceDetails(place.place_id)}
                >
                  <img
                    src={place.photo_url}
                    alt={place.name}
                    className="w-full h-48 object-cover rounded-t-xl"
                  />
                  <div className="p-2">
                    <h3 className="text-lg font-bold">{place.name}</h3>
                    <p className="text-gray-600">{place.formatted_address}</p>
                    {place.rating && (
                      <p className="text-yellow-500">⭐ {place.rating}</p>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>

      {/* Modal for Place Details */}
      {selectedPlace && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center"
          onClick={() => setSelectedPlace(null)}
        >
          <div
            className="bg-white rounded-xl shadow-lg p-6 w-11/12 md:w-2/3 lg:w-1/2"
            onClick={(e) => e.stopPropagation()}
          >
            <h2 className="text-2xl font-bold mb-2">{selectedPlace.name}</h2>
            <p className="mb-2">{selectedPlace.formatted_address}</p>
            {selectedPlace.rating && (
              <p className="text-yellow-500 mb-2">⭐ {selectedPlace.rating}</p>
            )}
            <h3 className="text-xl font-semibold mb-2">Customer Reviews:</h3>
            {selectedPlace.reviews ? (
              selectedPlace.reviews.map((review, idx) => (
                <div key={idx} className="mb-3 border-b pb-2">
                  <p className="font-bold">{review.author_name}</p>
                  <p className="text-yellow-500">⭐ {review.rating}</p>
                  <p className="text-gray-700">{review.text}</p>
                </div>
              ))
            ) : (
              <p className="text-gray-500">No reviews available.</p>
            )}
            <button
              className="mt-4 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              onClick={() => setSelectedPlace(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
