import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [location, setLocation] = useState("");
  const [places, setPlaces] = useState({});
  const [selectedPlace, setSelectedPlace] = useState(null);

  // Fetch places for a location
  const fetchPlaces = async (loc) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/places`, {
        params: { location: loc },
      });
      setPlaces(response.data);
      setSelectedPlace(null); // Clear previous selection
    } catch (error) {
      console.error("Error fetching places:", error);
    }
  };

  // Fetch place details for reviews
  const fetchPlaceDetails = async (place_id) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/place-details`, {
        params: { place_id },
      });
      setSelectedPlace(response.data);
    } catch (error) {
      console.error("Error fetching place details:", error);
    }
  };

  // Automatically get user location on page load
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          console.log("User location:", latitude, longitude);

          // Reverse geocode to get place name
          const geocodeRes = await axios.get(
            `https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&key=AIzaSyB3PgI153B24mtSoJvKSjCPQyEyDQqu3K8`
          );

          const locName = geocodeRes.data.results[0]?.formatted_address || "Unknown Location";
          console.log("Detected location:", locName);
          setLocation(locName);
          fetchPlaces(locName);
        },
        (err) => {
          console.error("Geolocation error:", err);
          alert("Could not get your location. Please enter manually.");
        }
      );
    } else {
      alert("Geolocation is not supported by this browser.");
    }
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold text-center mb-6">🌏 Local Explorer</h1>

      <div className="flex justify-center mb-4">
        <input
          type="text"
          placeholder="Enter location (e.g., Rampally)"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="border rounded px-4 py-2 w-80 mr-2"
        />
        <button
          onClick={() => fetchPlaces(location)}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Search
        </button>
      </div>

      {/* List of categories */}
      <div>
        {Object.keys(places).map((category) => (
          <div key={category} className="mb-6">
            <h2 className="text-2xl font-semibold mb-2 capitalize">{category}</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4">
              {places[category].map((place) => (
                <div
                  key={place.place_id}
                  className="bg-white rounded shadow p-3 cursor-pointer hover:scale-105 transition"
                  onClick={() => fetchPlaceDetails(place.place_id)}
                >
                  <img
                    src={place.photo_url}
                    alt={place.name}
                    onError={(e) => {
                      if (!e.target.dataset.fallback) {
                        e.target.src = "https://via.placeholder.com/400x300.png?text=No+Image";
                        e.target.dataset.fallback = true;
                      }
                    }}
                    className="w-full h-48 object-cover rounded mb-2"
                  />
                  <h3 className="text-lg font-medium">{place.name}</h3>
                  <p className="text-gray-600">{place.formatted_address}</p>
                  <p className="text-yellow-500">⭐ {place.rating}</p>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>

      {/* Details and Reviews */}
      {selectedPlace && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-2xl">
            <h2 className="text-2xl font-bold mb-2">{selectedPlace.name}</h2>
            <p className="mb-2">{selectedPlace.formatted_address}</p>
            <p className="text-yellow-500 mb-4">⭐ {selectedPlace.rating}</p>
            <h3 className="text-xl font-semibold mb-2">Reviews</h3>
            <div className="space-y-2 max-h-60 overflow-y-auto">
              {selectedPlace.reviews?.length > 0 ? (
                selectedPlace.reviews.map((review, index) => (
                  <div
                    key={index}
                    className="border rounded p-2 bg-gray-50"
                  >
                    <p className="font-medium">{review.author_name}</p>
                    <p className="text-yellow-500">⭐ {review.rating}</p>
                    <p>{review.text}</p>
                  </div>
                ))
              ) : (
                <p>No reviews available.</p>
              )}
            </div>
            <button
              onClick={() => setSelectedPlace(null)}
              className="mt-4 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
