import React, { useState } from "react";
import axios from "axios";

function App() {
  const [location, setLocation] = useState("");
  const [places, setPlaces] = useState({});
  const [loading, setLoading] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [selectedPlace, setSelectedPlace] = useState(null);

  const categories = ["schools", "colleges", "theaters", "medical shops"];

  const fetchPlaces = async (loc = location) => {
    if (!loc) return alert("Please enter a location");
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/places?location=${loc}`);
      setPlaces(response.data);
    } catch (error) {
      console.error("Error fetching places:", error);
      alert("Failed to fetch places.");
    }
    setLoading(false);
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
  };

  const fetchPlaceDetails = async (placeId) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/place-details?place_id=${placeId}`);
      setSelectedPlace(response.data);
    } catch (error) {
      console.error("Error fetching place details:", error);
      alert("Failed to fetch place details.");
    }
  };

  const detectLocation = () => {
    if (navigator.geolocation) {
      setLoading(true);
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          try {
            const { latitude, longitude } = position.coords;
            // Reverse geocoding to get location name
            const response = await axios.get(
              `https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&key=AIzaSyDki4k3wRgR7TEw1ugkkaWqe1tBxxnDfDk`
            );
            const address = response.data.results[0].formatted_address;
            setLocation(address);
            fetchPlaces(address);
          } catch (error) {
            console.error("Error getting location:", error);
            alert("Could not get your location name. Please enter manually.");
            setLoading(false);
          }
        },
        (error) => {
          console.error("Geolocation error:", error);
          alert("Could not detect your location. Please enter manually.");
          setLoading(false);
        }
      );
    } else {
      alert("Geolocation is not supported by your browser. Please enter location manually.");
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-gradient-to-r from-orange-500 to-red-500 text-white p-6 text-center">
        <h1 className="text-3xl font-bold">🍲 Local Explorer</h1>
        <p className="mt-1">Discover Schools, Colleges, Theaters & Medical Shops Nearby</p>
      </header>

      <div className="flex justify-center mt-4 space-x-2">
        {categories.map((cat) => (
          <button
            key={cat}
            onClick={() => handleCategoryClick(cat)}
            className={`px-4 py-2 rounded-full text-white ${selectedCategory === cat ? "bg-red-600" : "bg-orange-400"} hover:bg-red-500`}
          >
            {cat.charAt(0).toUpperCase() + cat.slice(1)}
          </button>
        ))}
      </div>

      <div className="flex justify-center my-6">
        <input
          type="text"
          placeholder="Enter location (e.g., Hyderabad)"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="border border-gray-300 rounded-l px-4 py-2 w-1/3"
        />
        <button
          onClick={() => fetchPlaces()}
          className="bg-red-500 text-white px-4 py-2 hover:bg-red-600"
        >
          Search
        </button>
        <button
          onClick={detectLocation}
          className="bg-blue-500 text-white px-4 py-2 rounded-r hover:bg-blue-600"
          title="Auto-detect my location"
        >
          📍
        </button>
      </div>

      {loading && <p className="text-center text-gray-600">Loading...</p>}

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 p-4">
        {selectedCategory &&
          places[selectedCategory]?.map((place, index) => (
            <div
              key={index}
              onClick={() => fetchPlaceDetails(place.place_id)}
              className="bg-white rounded-lg shadow hover:shadow-lg transition duration-300 cursor-pointer"
            >
              <img
                src={place.photo_url}
                alt={place.name}
                className="w-full h-48 object-cover rounded-t"
              />
              <div className="p-4">
                <h3 className="text-xl font-semibold">{place.name}</h3>
                <p className="text-gray-600">{place.formatted_address}</p>
                {place.rating && <p className="text-yellow-500">⭐ {place.rating}</p>}
              </div>
            </div>
          ))}
      </div>

      {/* Modal */}
      {selectedPlace && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50"
          onClick={() => setSelectedPlace(null)}
        >
          <div
            className="bg-white rounded-lg shadow-lg p-6 w-11/12 md:w-2/3 lg:w-1/2"
            onClick={(e) => e.stopPropagation()}
          >
            <h2 className="text-2xl font-bold mb-2">{selectedPlace.name}</h2>
            <p>{selectedPlace.formatted_address}</p>
            {selectedPlace.rating && (
              <p className="text-yellow-500 mb-2">⭐ {selectedPlace.rating}</p>
            )}
            <button
              className="mt-4 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              onClick={() => setSelectedPlace(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
