import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  TextField,
  Card,
  CardContent,
  Typography,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  Button,
  Grid,
  Switch,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import { Loader } from "@googlemaps/js-api-loader";
import { motion } from "framer-motion";

const App = () => {
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [darkMode, setDarkMode] = useState(false);
  const [filterType, setFilterType] = useState("");
  const [locationFetched, setLocationFetched] = useState(false);

  const fetchPlaces = async (location, type = "") => {
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:5000/api/places?location=${location}&type=${type}`
      );
      setPlaces(response.data.results);
    } catch (error) {
      console.error("Error fetching places:", error);
    }
    setLoading(false);
  };

  useEffect(() => {
    const loader = new Loader({
      apiKey: "AIzaSyB3PgI153B24mtSoJvKSjCPQyEyDQqu3K8",
      libraries: ["places"],
    });

    loader.load().then(() => {
      const input = document.getElementById("locationInput");
      const autocomplete = new window.google.maps.places.Autocomplete(input);
      autocomplete.addListener("place_changed", () => {
        const place = autocomplete.getPlace();
        if (place.formatted_address) {
          fetchPlaces(place.formatted_address, filterType);
        }
      });
    });
  }, [filterType]);

  useEffect(() => {
    if (!locationFetched) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const coords = `${position.coords.latitude},${position.coords.longitude}`;
          fetchPlaces(coords, filterType);
          setLocationFetched(true);
        },
        (error) => {
          console.error("Geolocation error:", error);
        }
      );
    }
  }, [filterType, locationFetched]);

  const handleCardClick = (place) => {
    setSelectedPlace(place);
  };

  const handleClose = () => {
    setSelectedPlace(null);
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        background: darkMode ? "#121212" : "#f0f2f5",
        color: darkMode ? "#fff" : "#000",
        padding: "2rem",
        transition: "all 0.3s ease",
      }}
    >
      <Typography variant="h3" align="center" gutterBottom>
        🌍 Local Explorer
      </Typography>

      <div style={{ display: "flex", justifyContent: "center", gap: "1rem", marginBottom: "1.5rem" }}>
        <TextField
          id="locationInput"
          placeholder="Search location e.g. Rampally"
          variant="outlined"
          fullWidth
          style={{
            maxWidth: 400,
            backgroundColor: darkMode ? "#333" : "#fff",
            borderRadius: 8,
          }}
        />
        <FormControl variant="outlined" style={{ minWidth: 150 }}>
          <InputLabel>Filter</InputLabel>
          <Select
            value={filterType}
            onChange={(e) => setFilterType(e.target.value)}
            label="Filter"
          >
            <MenuItem value="">All</MenuItem>
            <MenuItem value="school">Schools</MenuItem>
            <MenuItem value="hospital">Hospitals</MenuItem>
            <MenuItem value="restaurant">Restaurants</MenuItem>
            <MenuItem value="movie_theater">Theaters</MenuItem>
            <MenuItem value="pharmacy">Pharmacies</MenuItem>
          </Select>
        </FormControl>
        <div>
          <Typography variant="body2">🌙 Dark Mode</Typography>
          <Switch checked={darkMode} onChange={toggleDarkMode} />
        </div>
      </div>

      {loading ? (
        <div style={{ display: "flex", justifyContent: "center", marginTop: "3rem" }}>
          <CircularProgress color={darkMode ? "secondary" : "primary"} />
        </div>
      ) : (
        <Grid container spacing={3}>
          {places.map((place, idx) => (
            <Grid item xs={12} sm={6} md={4} key={idx}>
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: idx * 0.05 }}
              >
                <Card
                  style={{
                    borderRadius: 16,
                    background: darkMode
                      ? "rgba(255, 255, 255, 0.05)"
                      : "rgba(255, 255, 255, 0.8)",
                    backdropFilter: "blur(10px)",
                    boxShadow: darkMode
                      ? "0 8px 24px rgba(0,0,0,0.8)"
                      : "0 8px 24px rgba(0,0,0,0.2)",
                    cursor: "pointer",
                    color: darkMode ? "#fff" : "#000",
                  }}
                  onClick={() => handleCardClick(place)}
                >
                  <img
                    src={
                      place.photos
                        ? `https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photoreference=${place.photos[0].photo_reference}&key=AIzaSyB3PgI153B24mtSoJvKSjCPQyEyDQqu3K8`
                        : "https://via.placeholder.com/400x300?text=No+Image"
                    }
                    alt={place.name}
                    style={{
                      width: "100%",
                      height: 200,
                      objectFit: "cover",
                      borderTopLeftRadius: 16,
                      borderTopRightRadius: 16,
                    }}
                  />
                  <CardContent>
                    <Typography variant="h6">{place.name}</Typography>
                    <Typography variant="body2">{place.formatted_address}</Typography>
                    {place.rating && (
                      <Typography variant="body2" color="secondary">
                        ⭐ {place.rating} ({place.user_ratings_total} reviews)
                      </Typography>
                    )}
                    <Typography
                      variant="body2"
                      style={{ marginTop: "0.5rem", fontStyle: "italic" }}
                    >
                      {place.types?.slice(0, 3).join(", ")}
                    </Typography>
                  </CardContent>
                </Card>
              </motion.div>
            </Grid>
          ))}
        </Grid>
      )}

      <Dialog open={!!selectedPlace} onClose={handleClose} fullWidth maxWidth="sm">
        <DialogTitle>{selectedPlace?.name}</DialogTitle>
        <DialogContent>
          <Typography gutterBottom>
            📍 {selectedPlace?.formatted_address}
          </Typography>
          <Typography variant="body2" color="textSecondary" gutterBottom>
            ⭐ {selectedPlace?.rating} ({selectedPlace?.user_ratings_total} reviews)
          </Typography>
          <ul>
            {selectedPlace?.reviews
              ? selectedPlace.reviews.map((review, index) => (
                  <li key={index}>
                    <strong>{review.author_name}:</strong> {review.text}
                  </li>
                ))
              : "No reviews available."}
          </ul>
        </DialogContent>
        <Button
          onClick={handleClose}
          style={{ margin: "1rem" }}
          variant="contained"
          color="primary"
        >
          Close
        </Button>
      </Dialog>
    </div>
  );
};

export default App;
