import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [location, setLocation] = useState("");
  const [places, setPlaces] = useState({});
  const [loading, setLoading] = useState(false);
  const [selectedPlace, setSelectedPlace] = useState(null);

  const defaultPhoto =
    "https://source.unsplash.com/400x300/?locality,city,street";

  const fetchPlaces = async (loc) => {
    if (!loc) return alert("Please enter a location");
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:5000/api/places?location=${loc}`
      );
      setPlaces(response.data);
    } catch (error) {
      console.error("Error fetching places:", error);
      alert("Failed to fetch places.");
    }
    setLoading(false);
  };

  const detectLocationAndFetch = () => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          try {
            const res = await axios.get(
              `https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&key=YOUR_API_KEY_HERE`
            );
            const userLocation = res.data.results[0].formatted_address;
            setLocation(userLocation);
            fetchPlaces(userLocation);
          } catch (err) {
            console.error("Failed to reverse geocode:", err);
            alert("Could not detect your location.");
          }
        },
        (error) => {
          console.error("Geolocation error:", error);
          alert("Location access denied. Please search manually.");
        }
      );
    } else {
      alert("Geolocation is not supported by your browser.");
    }
  };

  useEffect(() => {
    detectLocationAndFetch();
  }, []);

  const fetchPlaceDetails = async (placeId) => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/place-details?place_id=${placeId}`
      );
      setSelectedPlace(response.data);
    } catch (error) {
      console.error("Error fetching place details:", error);
      alert("Failed to fetch place details.");
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      <h1 className="text-4xl font-extrabold text-center mb-8 text-blue-600">
        üåü Local Explorer
      </h1>

      <div className="flex justify-center mb-6">
        <input
          type="text"
          placeholder="Enter location (e.g., Hyderabad)"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="border border-gray-300 rounded-l px-4 py-2 w-1/2"
        />
        <button
          onClick={() => fetchPlaces(location)}
          className="bg-blue-600 text-white px-4 py-2 rounded-r hover:bg-blue-700"
        >
          Search
        </button>
      </div>

      {loading && (
        <p className="text-center text-lg text-gray-600">Loading places...</p>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {Object.entries(places).map(([category, placesArray]) => (
          <div key={category}>
            <h2 className="text-2xl font-semibold mb-4 capitalize text-blue-500">
              {category}
            </h2>
            {placesArray.map((place, index) => (
              <div
                key={index}
                className="bg-white rounded-lg shadow-lg p-4 mb-4 cursor-pointer hover:shadow-xl transition duration-300"
                onClick={() => fetchPlaceDetails(place.place_id)}
              >
                <img
                  src={place.photo_url || defaultPhoto}
                  alt={place.name}
                  className="w-full h-48 object-cover rounded mb-2"
                  onError={(e) => (e.target.src = defaultPhoto)}
                />
                <h3 className="text-lg font-bold">{place.name}</h3>
                <p className="text-gray-600">{place.formatted_address}</p>
                {place.rating && (
                  <p className="text-yellow-500">‚≠ê {place.rating}</p>
                )}
              </div>
            ))}
          </div>
        ))}
      </div>

      {selectedPlace && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center"
          onClick={() => setSelectedPlace(null)}
        >
          <div
            className="bg-white rounded-lg shadow p-6 w-11/12 md:w-2/3 lg:w-1/2"
            onClick={(e) => e.stopPropagation()}
          >
            <h2 className="text-2xl font-bold mb-2">{selectedPlace.name}</h2>
            <p className="mb-2">{selectedPlace.formatted_address}</p>
            {selectedPlace.rating && (
              <p className="text-yellow-500 mb-2">‚≠ê {selectedPlace.rating}</p>
            )}

            <h3 className="text-xl font-semibold mb-2">Customer Reviews:</h3>
            {selectedPlace.reviews ? (
              selectedPlace.reviews.map((review, idx) => (
                <div key={idx} className="mb-3 border-b pb-2">
                  <p className="font-bold">{review.author_name}</p>
                  <p className="text-yellow-500">‚≠ê {review.rating}</p>
                  <p className="text-gray-700">{review.text}</p>
                </div>
              ))
            ) : (
              <p className="text-gray-500">No reviews available.</p>
            )}

            <button
              className="mt-4 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              onClick={() => setSelectedPlace(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
