import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [location, setLocation] = useState("");
  const [places, setPlaces] = useState({});
  const [loading, setLoading] = useState(false);

  const fetchPlaces = async () => {
    if (!location) return alert("Please enter a location");
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:5000/api/places?location=${location}`
      );
      setPlaces(response.data);
    } catch (error) {
      console.error("Error fetching places:", error);
      alert("Failed to fetch places.");
    }
    setLoading(false);
  };

  useEffect(() => {
    // Auto-detect location once
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          try {
            const response = await axios.get(
              `https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&key=AIzaSyDki4k3wRgR7TEw1ugkkaWqe1tBxxnDfDk`
            );
            const userLocation = response.data.results[0].formatted_address;
            setLocation(userLocation);
            fetchPlaces();
          } catch (error) {
            console.error("Geolocation error:", error);
          }
        },
        (err) => {
          console.error("Geolocation permission denied:", err);
        }
      );
    }
    // eslint-disable-next-line
  }, []);

  const categoryIcons = {
    schools: "🏫",
    colleges: "🎓",
    theaters: "🎬",
    "medical shops": "💊"
  };

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      <h1 className="text-3xl font-bold text-center mb-6">🌍 Local Explorer</h1>

      <div className="flex justify-center mb-6">
        <input
          type="text"
          placeholder="Enter location (e.g., Hyderabad)"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="border border-gray-300 rounded-l px-4 py-2 w-1/3"
        />
        <button
          onClick={fetchPlaces}
          className="bg-blue-500 text-white px-4 py-2 rounded-r hover:bg-blue-600"
        >
          Search
        </button>
      </div>

      {loading && <p className="text-center text-lg">Loading places...</p>}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {Object.entries(places).map(([category, placesArray]) => (
          <div key={category} className="mb-8">
            <h2 className="text-2xl font-bold mb-4">
              {categoryIcons[category]} {category}
            </h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              {placesArray.map((place, index) => (
                <div
                  key={index}
                  className="bg-white rounded-lg shadow hover:shadow-lg transition p-2"
                >
                  <img
                    src={place.photo_url}
                    alt={place.name}
                    className="w-full h-48 object-cover rounded mb-2"
                  />
                  <h3 className="text-lg font-bold">{place.name}</h3>
                  <p className="text-gray-600 text-sm">{place.formatted_address}</p>
                  {place.rating && (
                    <p className="text-yellow-500 text-sm">⭐ {place.rating}</p>
                  )}
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
