import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  TextField,
  Card,
  CardContent,
  Typography,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  Button,
  Grid,
  MenuItem,
  Select,
  InputLabel,
  FormControl,
} from "@mui/material";
import { Loader } from "@googlemaps/js-api-loader";
import { motion } from "framer-motion";

const App = () => {
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [filterType, setFilterType] = useState(""); // 🔥 Filter state
  const [currentLocation, setCurrentLocation] = useState(""); // 🔥 Store current location

  const fetchPlaces = async (location, type = "") => {
    if (!location) return;
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:5000/api/places?location=${location}&type=${type}`
      );
      setPlaces(response.data.results);
    } catch (error) {
      console.error("Error fetching places:", error);
    }
    setLoading(false);
  };

  useEffect(() => {
    const loader = new Loader({
      apiKey: "AIzaSyB3PgI153B24mtSoJvKSjCPQyEyDQqu3K8",
      libraries: ["places"],
    });

    loader.load().then(() => {
      const input = document.getElementById("locationInput");
      const autocomplete = new window.google.maps.places.Autocomplete(input);

      autocomplete.addListener("place_changed", () => {
        const place = autocomplete.getPlace();
        if (place.formatted_address) {
          setCurrentLocation(place.formatted_address);
          fetchPlaces(place.formatted_address, filterType);
        }
      });

      // Auto-detect current location
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const coords = `${position.coords.latitude},${position.coords.longitude}`;
          setCurrentLocation(coords);
          fetchPlaces(coords, filterType);
        },
        (error) => {
          console.error("Geolocation error:", error);
        }
      );
    });
  }, []);

  // 🔥 Re-fetch when filterType changes
  useEffect(() => {
    if (currentLocation) {
      fetchPlaces(currentLocation, filterType);
    }
  }, [filterType]);

  const handleCardClick = (place) => {
    setSelectedPlace(place);
  };

  const handleClose = () => {
    setSelectedPlace(null);
  };

  return (
    <div style={{ minHeight: "100vh", background: "#f0f2f5", padding: "2rem" }}>
      <Typography variant="h3" align="center" style={{ marginBottom: "2rem" }}>
        🌍 Local Explorer
      </Typography>

      <Grid container spacing={2} justifyContent="center" style={{ marginBottom: "2rem" }}>
        <Grid item xs={12} sm={6} md={4}>
          <TextField
            id="locationInput"
            placeholder="Search a location e.g. Rampally"
            variant="outlined"
            fullWidth
            style={{ backgroundColor: "#fff", borderRadius: 8 }}
          />
        </Grid>
        <Grid item xs={12} sm={3} md={2}>
          <FormControl fullWidth>
            <InputLabel>Filter</InputLabel>
            <Select
              value={filterType}
              onChange={(e) => setFilterType(e.target.value)}
            >
              <MenuItem value="">All</MenuItem>
              <MenuItem value="school">Schools</MenuItem>
              <MenuItem value="hospital">Hospitals</MenuItem>
              <MenuItem value="restaurant">Restaurants</MenuItem>
              <MenuItem value="movie_theater">Theaters</MenuItem>
              <MenuItem value="pharmacy">Pharmacies</MenuItem>
            </Select>
          </FormControl>
        </Grid>
      </Grid>

      {loading ? (
        <div style={{ display: "flex", justifyContent: "center", marginTop: "3rem" }}>
          <CircularProgress />
        </div>
      ) : (
        <Grid container spacing={3}>
          {places.map((place, idx) => (
            <Grid item xs={12} sm={6} md={4} key={idx}>
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: idx * 0.05 }}
              >
                <Card
                  style={{
                    borderRadius: 16,
                    boxShadow: "0 4px 20px rgba(0,0,0,0.2)",
                    cursor: "pointer",
                  }}
                  onClick={() => handleCardClick(place)}
                >
                  <img
                    src={
                      place.photos
                        ? `https://maps.googleapis.com/maps/api/place/photo?maxwidth=400&photoreference=${place.photos[0].photo_reference}&key=AIzaSyB3PgI153B24mtSoJvKSjCPQyEyDQqu3K8`
                        : "https://via.placeholder.com/400x300?text=No+Image"
                    }
                    alt={place.name}
                    style={{
                      width: "100%",
                      height: 200,
                      objectFit: "cover",
                      borderTopLeftRadius: 16,
                      borderTopRightRadius: 16,
                    }}
                    onError={(e) => {
                      e.target.src = "https://via.placeholder.com/400x300?text=No+Image";
                    }}
                  />
                  <CardContent>
                    <Typography variant="h6">{place.name}</Typography>
                    <Typography variant="body2" color="textSecondary">
                      {place.vicinity || place.formatted_address}
                    </Typography>
                  </CardContent>
                </Card>
              </motion.div>
            </Grid>
          ))}
        </Grid>
      )}

      <Dialog open={!!selectedPlace} onClose={handleClose} fullWidth maxWidth="sm">
        <DialogTitle>{selectedPlace?.name}</DialogTitle>
        <DialogContent>
          <Typography variant="body1" gutterBottom>
            Address: {selectedPlace?.vicinity || selectedPlace?.formatted_address}
          </Typography>
          <Typography variant="body2" color="textSecondary">
            Reviews:
          </Typography>
          <ul>
            {selectedPlace?.reviews
              ? selectedPlace.reviews.map((review, index) => (
                  <li key={index}>
                    <strong>{review.author_name}:</strong> {review.text}
                  </li>
                ))
              : "No reviews available."}
          </ul>
        </DialogContent>
        <Button
          onClick={handleClose}
          style={{ margin: "1rem" }}
          variant="contained"
          color="primary"
        >
          Close
        </Button>
      </Dialog>
    </div>
  );
};

export default App;
