import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  TextField,
  Card,
  CardContent,
  Typography,
  CircularProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  Button,
  Grid,
} from "@mui/material";
import { Loader } from "@googlemaps/js-api-loader";
import { motion } from "framer-motion";

const App = () => {
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [currentLocation, setCurrentLocation] = useState("Rampally, Telangana");
  const [filterType, setFilterType] = useState("all");

  const fetchPlaces = async (location, type = "all") => {
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:5000/api/places?location=${location}&type=${type}`
      );
      setPlaces(response.data.results);
    } catch (error) {
      console.error("Error fetching places:", error);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchPlaces(currentLocation, filterType);
  }, [currentLocation, filterType]);

  useEffect(() => {
    const loader = new Loader({
      apiKey: "AIzaSyB3PgI153B24mtSoJvKSjCPQyEyDQqu3K8",
      libraries: ["places"],
    });

    loader.load().then(() => {
      const input = document.getElementById("locationInput");
      const autocomplete = new window.google.maps.places.Autocomplete(input);
      autocomplete.addListener("place_changed", () => {
        const place = autocomplete.getPlace();
        if (place.formatted_address) {
          setCurrentLocation(place.formatted_address);
        }
      });
    });
  }, []);

  const handleCardClick = (place) => {
    setSelectedPlace(place);
  };

  const handleClose = () => {
    setSelectedPlace(null);
  };

  const filterButtons = [
    { label: "ALL", value: "all" },
    { label: "SCHOOLS", value: "school" },
    { label: "HOSPITALS", value: "hospital" },
    { label: "RESTAURANTS", value: "restaurant" },
    { label: "THEATERS", value: "movie_theater" },
    { label: "MEDICAL SHOPS", value: "pharmacy" },
  ];

  return (
    <div style={{ minHeight: "100vh", background: "#f9fafc", padding: "2rem" }}>
      <Typography
        variant="h3"
        align="center"
        style={{ marginBottom: "1rem", color: "#1976d2", fontWeight: "bold" }}
      >
        🌍 Local Explorer
      </Typography>

      <div
        style={{
          display: "flex",
          justifyContent: "center",
          marginBottom: "1rem",
        }}
      >
        <TextField
          id="locationInput"
          placeholder="Search a location e.g. Rampally"
          variant="outlined"
          fullWidth
          style={{
            maxWidth: 600,
            backgroundColor: "#fff",
            borderRadius: 8,
            boxShadow: "0px 2px 5px rgba(0, 0, 0, 0.1)",
          }}
          value={currentLocation}
          onChange={(e) => setCurrentLocation(e.target.value)}
        />
      </div>

      <div
        style={{
          display: "flex",
          justifyContent: "center",
          gap: "0.5rem",
          flexWrap: "wrap",
          marginBottom: "1.5rem",
        }}
      >
        {filterButtons.map((btn) => (
          <Button
            key={btn.value}
            variant={filterType === btn.value ? "contained" : "outlined"}
            color="primary"
            onClick={() => setFilterType(btn.value)}
          >
            {btn.label}
          </Button>
        ))}
      </div>

      {loading ? (
        <div style={{ display: "flex", justifyContent: "center", marginTop: "3rem" }}>
          <CircularProgress color="primary" />
        </div>
      ) : (
        <Grid container spacing={3}>
          {places.map((place, idx) => (
            <Grid item xs={12} sm={6} md={4} key={idx}>
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: idx * 0.05 }}
              >
                <Card
                  style={{
                    borderRadius: 16,
                    boxShadow: "0 6px 15px rgba(0, 0, 0, 0.1)",
                    cursor: "pointer",
                    transition: "transform 0.2s ease",
                  }}
                  onClick={() => handleCardClick(place)}
                  onMouseEnter={(e) => (e.currentTarget.style.transform = "scale(1.03)")}
                  onMouseLeave={(e) => (e.currentTarget.style.transform = "scale(1)")}
                >
                  <img
                    src={
                      place.photos
                        ? `https://maps.googleapis.com/maps/api/place/photo?maxwidth=600&photoreference=${place.photos[0].photo_reference}&key=AIzaSyB3PgI153B24mtSoJvKSjCPQyEyDQqu3K8`
                        : "https://via.placeholder.com/600x400?text=No+Image"
                    }
                    alt={place.name}
                    style={{
                      width: "100%",
                      height: 200,
                      objectFit: "cover",
                      borderTopLeftRadius: 16,
                      borderTopRightRadius: 16,
                    }}
                    onError={(e) => {
                      e.target.src = "https://via.placeholder.com/600x400?text=No+Image";
                    }}
                  />
                  <CardContent>
                    <Typography
                      variant="h6"
                      style={{ fontWeight: "600", marginBottom: "0.3rem" }}
                    >
                      {place.name}
                    </Typography>
                    <Typography
                      variant="body2"
                      color="textSecondary"
                      style={{ lineHeight: 1.4 }}
                    >
                      {place.formatted_address}
                    </Typography>
                  </CardContent>
                </Card>
              </motion.div>
            </Grid>
          ))}
        </Grid>
      )}

      <Dialog open={!!selectedPlace} onClose={handleClose} fullWidth maxWidth="sm">
        <DialogTitle>{selectedPlace?.name}</DialogTitle>
        <DialogContent>
          <Typography variant="body1" gutterBottom>
            Address: {selectedPlace?.formatted_address}
          </Typography>
          <Typography variant="body2" color="textSecondary">
            Reviews (if available):
          </Typography>
          <ul>
            {selectedPlace?.reviews
              ? selectedPlace.reviews.map((review, index) => (
                  <li key={index}>
                    <strong>{review.author_name}:</strong> {review.text}
                  </li>
                ))
              : "No reviews available."}
          </ul>
        </DialogContent>
        <Button
          onClick={handleClose}
          style={{ margin: "1rem" }}
          variant="contained"
          color="primary"
        >
          Close
        </Button>
      </Dialog>
    </div>
  );
};

export default App;
